import { useEffect, useRef } from "react";
import "./ss.css";
import img from "./b4image.png"; // use your image

export default function SS() {
  const stageRef = useRef(null);
  const cardsRef = useRef([]);

  useEffect(() => {
    function updateActive() {
      const stage = stageRef.current;
      if (!stage) return;

      const rect = stage.getBoundingClientRect();
      const scrollHeight = stage.offsetHeight - window.innerHeight;

      const progress = Math.min(
        Math.max(-rect.top / scrollHeight, 0),
        1
      );

      const index = Math.round(progress * 5);

      cardsRef.current.forEach((card, i) => {
        if (i === index) card.classList.add("active");
        else card.classList.remove("active");
      });
    }

    window.addEventListener("scroll", updateActive);
    updateActive();

    return () => window.removeEventListener("scroll", updateActive);
  }, []);

  return (
    <section className="ss-stage" ref={stageRef}>
      <div className="ss-wrapper">

        {[
          "Your MD sets your clinical goals.",
          "You create your own AI Doctor.",
          "Your devices connect and your data flows automatically.",
          "Your AI Doctor builds a digital model of your body.",
          "You receive daily individualized guidance.",
          "A clinician supervises everything and steps in when needed."
        ].map((text, i) => (
          <div
            key={i}
            className="ss-card"
            ref={(el) => (cardsRef.current[i] = el)}
          >
            <img src={img} alt="model" />
            <p>{text}</p>
            <span>{String(i + 1).padStart(2, "0")}</span>
            <p className="po">After sign-up, your own physician (or one you choose on the platform) reviews your labs, medications, history, and current biomarkers.  They set measurable 3–6 month goals — for HbA1c, weight, LDL, blood pressure, sleep, and more.  These goals become the medical blueprint for your AI Doctor.</p>
          </div>
        ))}

      </div>
    </section>
  );
}
