// Sam.js
import React, { useRef, useEffect, useState } from 'react';
import './sam.css'; // Import the dedicated CSS file

// Dummy image imports (replace with actual image paths)
import img01 from './b4image.png';
import img02 from './b4image.png';
import img03 from './b4image.png';
import img04 from './b4image.png';
import img05 from './b4image.png';
import img06 from './b4image.png';

const content = [
  { id: 1, number: '01', image: img01 },
  { id: 2, number: '02', image: img02 },
  { id: 3, number: '03', image: img03 },
  { id: 4, number: '04', image: img04 },
  { id: 5, number: '05', image: img05 },
  { id: 6, number: '06', image: img06 },
];

const Sam = () => {
  const [activeNumberId, setActiveNumberId] = useState(1);
  const rowRefs = useRef([]);

  useEffect(() => {
    const observerOptions = {
      // Root is the viewport
      root: null,
      // Target an area covering the middle 10% of the viewport (45% to 55%)
      // This is crucial for detecting when the number is at the *center*.
      rootMargin: '-45% 0px -45% 0px', 
      threshold: 0, // We just need to know if it's visible in the rootMargin area
    };

    const observer = new IntersectionObserver((entries) => {
      entries.forEach(entry => {
        const rowId = parseInt(entry.target.dataset.id);
        
        // If the row's center (number) is intersecting the middle of the screen
        if (entry.isIntersecting) {
          setActiveNumberId(rowId);
        }
      });
    }, observerOptions);

    // Attach observer to each row
    rowRefs.current.forEach(ref => {
      if (ref) observer.observe(ref);
    });

    // Clean up observer on component unmount
    return () => {
      rowRefs.current.forEach(ref => {
        if (ref) observer.unobserve(ref);
      });
    };
  }, []);

  return (
    <div className="sam-container">
      {content.map((item, index) => (
        <div
          key={item.id}
          ref={el => rowRefs.current[index] = el}
          data-id={item.id}
          className="sam-row"
        >
          {/* Left Column (Sticky Number) */}
          <div 
            className="sam-col left-col"
            // Set z-index for the overlapping effect (Req. 6 & 10)
            style={{ zIndex: 6 - index }} 
          >
            <div className={`number-wrapper ${activeNumberId === item.id ? 'sticky' : ''}`}>
                {item.number}
            </div>
          </div>

          {/* Right Column (Image) */}
          <div className="sam-col right-col">
            <img src={item.image} alt={`Content ${item.number}`} className="row-image" />
          </div>
        </div>
      ))}
    </div>
  );
};

export default Sam;
