import React, { useRef, useState, useEffect } from "react";
import illu01 from "./Frame 109.png";
import illu02 from "./Frame 109.png";
import illu03 from "./Frame 109.png";
import illu04 from "./Frame 109.png";
import illu05 from "./Frame 109.png";
import illu06 from "./Frame 109.png";
import signup from "./Frame.png";


const illustrations =  [illu01, illu02, illu03, illu04, illu05, illu06];

const content = [
  { number: "01", heading: "Your data stays yours", body: "Your health data is encrypted, never sold, and never shared without your permission." },
  { number: "02", heading: "Built for safety first", body: "AI Doctor avoids harmful or risky recommendations and escalates to clinicians." },
  { number: "03", heading: "Transparent and in your control", body: "You can see why the AI makes decisions. No black boxes." },
  { number: "04", heading: "Medically validated", body: "Every AI Doctor is reviewed by licensed physicians." },
  { number: "05", heading: "Regulation compliant", body: "Complies with HIPAA and medical AI safeguards." },
  { number: "06", heading: "Human doctors oversee everything", body: "Real doctors monitor all cases alongside the AI." }
];

export default function ScrollCarousel() {
  const [sections, setSections] = useState([]);
  const containerRef = useRef(null);
  const sectionRefs = useRef([]);

  useEffect(() => {
    const handleScroll = () => {
      if (!containerRef.current) return;

      const container = containerRef.current;
      const viewportHeight = window.innerHeight;

      const totalHeight = container.offsetHeight - viewportHeight;
      const scrolled = window.scrollY - container.offsetTop;

      // ⭐ Must begin at 100% when component enters screen
      let globalProgress = 0;
      const enterDistance = window.scrollY + viewportHeight - container.offsetTop;

      if (enterDistance <= 0) {
        globalProgress = 0; // freeze at bottom (100%)
      } else {
        globalProgress = Math.max(0, Math.min(1, scrolled / totalHeight));
      }

      const newSections = sectionRefs.current.map((ref, index) => {
        if (!ref) return { linePosition: 100, isActive: false };

        const rect = ref.getBoundingClientRect();
        const viewportCenter = viewportHeight / 2;

        const isActive = rect.top <= viewportCenter && rect.bottom >= viewportCenter;

        // Map global progress per row
        const start = index / content.length;
        const end = (index + 1) / content.length;

        let linePosition = 100;
        if (globalProgress < start) linePosition = 100;
        else if (globalProgress > end) linePosition = 0;
        else linePosition = 100 - ((globalProgress - start) / (end - start)) * 100;

        return { linePosition, isActive };
      });

      setSections(newSections);
    };

    window.addEventListener("scroll", handleScroll);
    handleScroll();

    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <>
      <div className="scroll-carousel" ref={containerRef}>
        
        {/* LEFT FIXED PANEL */}
        <div className="carousel-left">
          {content.map((item, index) => {
            const sec = sections[index] || { linePosition: 100, isActive: index === 0 };

            return (
              <div key={index} className="content-card" style={{ opacity: sec.isActive ? 1 : 0 }}>
                
                {/* Reveal line */}
                <div className="reveal-line" style={{ top: `${sec.linePosition}%` }} />

                {/* Centered Left Content */}
                <div className="left-wrap center-left">
                  <img src={signup} className="signup-ico" />
                  <h1 className="lef-number">{item.number}</h1>
                  <div className="gradient-border" />
                  <h3 className="lef-title">{item.heading}</h3>
                  <p className="lef-desc">{item.body}</p>
                </div>

                {/* Mask */}
                <div className="content-mask" style={{
                  top: `${sec.linePosition}%`,
                  height: `${100 - sec.linePosition}%`
                }} />
              </div>
            );
          })}
        </div>

        {/* RIGHT SECTIONS */}
        <div className="carousel-right">
          {content.map((_, i) => (
            <div className="content-section" key={i} ref={el => (sectionRefs.current[i] = el)}>
              <img src={illustrations[i]} className="illu" />
            </div>
          ))}
        </div>
      </div>

      {/* INLINE CSS */}
      <style>{`
        .scroll-carousel {
          display: flex;
          min-height: 600vh;
          background: #000;
          width: 100%;
        }

        /* LEFT PANEL */
        .carousel-left {
          width: 40%;
          position: sticky;
          top: 0;
          height: 100vh;
          display: flex;
          align-items: center;
          justify-content: center;
          background: #000;
          overflow: hidden;
        }

        .content-card {
          position: absolute;
          width: 90%;
          height: 100%;

        }

        /* Reveal Line */
        .reveal-line {
          position: absolute;
          width: 100%;
          height: 2px;
          background: #fff;
          z-index: 10;
          transition: top 0.08s linear;
        }

        /* Mask */
        .content-mask {
          position: absolute;
          left: 0;
          width: 100%;
          background: #000;
          z-index: 9;
          transition: top 0.08s linear, height 0.08s linear;
        }

        /* LEFT CONTENT CENTERING (IMPORTANT!) */
        .center-left {
          display: flex;
          flex-direction: column;
          justify-content: center;   /* center vertically */
          height: 100%;
        }

        .signup-ico { width: 50px; margin-bottom: -150px;margin-left: 15%; }

        .lef-number { font-size: 90px; font-weight: 300; color: #fff; margin-left: 70px; }

        .gradient-border {
          width: 100%;
          height: 2px;
          background: linear-gradient(to right,#777,#000);
          margin: 10px 0 20px 0;
        }

        .lef-title { font-size: 36px; text-transform: uppercase; color: #fff; }

        .lef-desc { font-size: 18px; width: 90%; opacity: 0.75; color: #ccc; }

        /* RIGHT SIDE */
        .carousel-right {
          width: 60%;
          border-left: 1px solid #333;
        }

        .content-section {
          height: 100vh;
          display: flex;
          justify-content: center;
          align-items: center;
          background: #000;
        }

        .illu {
          width: 75%;
        }
      `}</style>
    </>
  );
}
