import { useEffect, useRef, useState } from "react";
import "./hiw.css";
import img from "./b4image.png";

function Hiw() {
  const sectionsRef = useRef([]);
  const [activeIndex, setActiveIndex] = useState(0);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            const index = Number(entry.target.dataset.index);
            setActiveIndex(index);
          }
        });
      },
      {
        threshold: 0.6,
      }
    );

    sectionsRef.current.forEach((section) => {
      if (section) observer.observe(section);
    });

    return () => observer.disconnect();
  }, []);

  return (
    <div className="hiw-container">

      {/* LEFT */}
      <div className="lft">
        {[
          "01 Your AI Doctor studies the way your body behaves",
          "02 Behind every insight is real clinical reasoning.",
          "03 Clear explanations. Simple actions. No jargon."
        ].map((text, i) => (
          <div
            key={i}
            className={`lft-box ${activeIndex === i ? "active" : ""}`}
            ref={(el) => (sectionsRef.current[i] = el)}
            data-index={i}
          >
            <p>{text}</p>
          </div>
        ))}
      </div>

      {/* RIGHT */}
      <div className="rgt">
        <img
          src={img}
          alt="hiw"
          className={`hiwimage ${activeIndex === 0 ? "show" : ""}`}
        />
        <img
          src={img}
          alt="hiw"
          className={`hiwimage ${activeIndex === 1 ? "show" : ""}`}
        />
        <img
          src={img}
          alt="hiw"
          className={`hiwimage ${activeIndex === 2 ? "show" : ""}`}
        />
      </div>

    </div>
  );
}

export default Hiw;
