import React, { useRef, useEffect, useState } from 'react';
import styled, { css } from 'styled-components';

// --- 1. CONFIGURATION AND DATA ---

// Define different colors for each row
const ROW_COLORS = [
    '#0e0d0d', // Dark Black (Base)
    '#250c0c', // Dark Red/Brown
    '#0c251e', // Dark Teal/Green
    '#25240c', // Dark Yellow/Mustard
    '#0c1525', // Dark Blue
    '#250c25', // Dark Purple
];

// Dummy Image URLs (for demonstration)
const dummyImage = "https://via.placeholder.com/600x800.png?text=Image";
const content = [
    { id: 1, number: '01', image: `${dummyImage}+01`, color: ROW_COLORS[0] },
    { id: 2, number: '02', image: `${dummyImage}+02`, color: ROW_COLORS[1] },
    { id: 3, number: '03', image: `${dummyImage}+03`, color: ROW_COLORS[2] },
    { id: 4, number: '04', image: `${dummyImage}+04`, color: ROW_COLORS[3] },
    { id: 5, number: '05', image: `${dummyImage}+05`, color: ROW_COLORS[4] },
    { id: 6, number: '06', image: `${dummyImage}+06`, color: ROW_COLORS[5] },
];

// --- 2. STYLED COMPONENTS (CSS-in-JS) ---

const GlobalStyle = css`
    body {
        background-color: ${ROW_COLORS[0]}; 
        color: white;
        margin: 0;
        padding: 0;
        /* Ensure smooth scrolling for the visual effect */
        scroll-behavior: smooth;
    }
`;

const SamContainer = styled.div`
    ${GlobalStyle}
    display: block;
    width: 100%;
`;

const SamRow = styled.div`
    display: flex;
    height: 100vh; 
    width: 100%;
    background-color: ${props => props.$bgColor || 'black'}; 
`;

const SamCol = styled.div`
    display: flex; 
    align-items: center;
    justify-content: center;
`;

// Left Column (40%)
const LeftCol = styled(SamCol)`
    flex: 0 0 40%; 
    position: relative; 
    /* This must be set to ensure the number disappears as the row scrolls away */
    overflow: hidden; 
    border-top: 1px solid white;
    border-bottom: 1px solid white; 
`;

// Right Column (60%)
const RightCol = styled(SamCol)`
    flex: 0 0 60%; 
`;

const RowImage = styled.img`
    max-width: 80%;
    max-height: 80%;
    object-fit: contain;
`;

/* Number Wrapper (Updated)
  - Not sticky: position: absolute. Its vertical position is calculated dynamically (yPos).
  - Sticky: position: fixed. Centered using top: 50% and transform: translateY(-50%).
*/
const NumberWrapper = styled.div.attrs(props => ({
    // Pass the calculated vertical position to be used in the transform style
    style: {
        transform: props.$isSticky 
            ? 'translateY(-50%)' 
            : `translateY(${props.$yPos}px)`,
        // Set z-index dynamically for the overlap effect (Req. 6)
        zIndex: props.$isSticky ? props.zIndex : 1,
        // Calculate opacity based on visibility state
        opacity: props.$isSticky || props.$isVisible ? 1 : 0,
        // Use position: fixed only when sticky
        position: props.$isSticky ? 'fixed' : 'absolute',
    },
}))`
    /* Base styles */
    top: 50%;
    right: 5%; 
    font-size: 5rem;
    font-weight: bold;
    color: white; 
    
    /* Transition for smooth animation between positions/states */
    transition: opacity 0.3s ease, transform 0.3s ease, color 0.3s ease, position 0.3s ease;
    
    /* Ensure the non-sticky number starts near the center */
    color: #444; 
    
    ${props => props.$isSticky && css`
        color: white; /* Active color */
        top: 50%;
        right: 5%;
    `}

    ${props => !props.$isSticky && css`
        /* When not sticky, position it absolutely within the LeftCol */
        position: absolute;
    `}
`;

// --- 3. React Component Logic ---

const Sam = () => {
    // Stores the ID of the number currently sticking to the center
    const [activeNumberId, setActiveNumberId] = useState(null);
    // Stores the scroll position of ALL numbers relative to their parent row
    const [rowPositions, setRowPositions] = useState({});
    
    const rowRefs = useRef([]);
    const observer = useRef(null);

    // Effect to handle Intersection Observing for the sticky state
    useEffect(() => {
        // Targets the middle 10% of the viewport (45% to 55%)
        const observerOptions = {
            root: null, 
            rootMargin: '-45% 0px -45% 0px', 
            threshold: 0, 
        };

        const callback = (entries) => {
            entries.forEach(entry => {
                const rowId = entry.target.dataset.id;
                
                if (entry.isIntersecting) {
                    // Row has entered the sticky zone (center of screen)
                    setActiveNumberId(parseInt(rowId));
                } else if (activeNumberId === parseInt(rowId) && !entry.isIntersecting) {
                    // The row just left the sticky zone (either above or below)
                    // We only update if this specific element was the last active one
                    setActiveNumberId(prevId => (prevId === parseInt(rowId) ? null : prevId));
                }
            });
        };

        observer.current = new IntersectionObserver(callback, observerOptions);

        rowRefs.current.forEach(ref => {
            if (ref) observer.current.observe(ref);
        });

        return () => {
            if (observer.current) {
                rowRefs.current.forEach(ref => {
                    if (ref) observer.current.unobserve(ref);
                });
            }
        };
    }, [activeNumberId]); // Re-run effect if activeNumberId changes to handle cleanup correctly

    // Effect to handle the dynamic scroll-in/scroll-out visibility and position
    useEffect(() => {
        const handleScroll = () => {
            const newPositions = {};
            rowRefs.current.forEach((ref, index) => {
                if (!ref) return;

                const rowId = content[index].id;
                const rect = ref.getBoundingClientRect();
                
                // Calculate how much the center of the row is off-center from the viewport
                const rowCenter = rect.top + rect.height / 2;
                const viewportCenter = window.innerHeight / 2;
                
                // deltaY is the vertical distance between the row center and the screen center.
                let deltaY = rowCenter - viewportCenter; 

                // We want the number to be perfectly centered (deltaY = 0) when sticky.
                // When not sticky, we shift it based on the scroll.
                
                let yPos;

                if (rowId === activeNumberId) {
                    // When active, the position is controlled by position: fixed, so we don't apply deltaY here.
                    yPos = 0; // Not used, but kept for clarity
                } else {
                    // When scrolling into the viewport, deltaY goes from +50vh to 0.
                    // When scrolling out, deltaY goes from 0 to -50vh.
                    
                    // We want the number to track the row's scroll, but offset it so it appears to scroll
                    // from the bottom into the center, and from the center out through the top.
                    
                    // The number starts 100px below the center and moves up as the row scrolls.
                    yPos = deltaY; 
                }

                newPositions[rowId] = yPos;
            });
            setRowPositions(newPositions);
        };

        window.addEventListener('scroll', handleScroll);
        // Initial call to set positions correctly on load
        handleScroll();

        return () => window.removeEventListener('scroll', handleScroll);
    }, [activeNumberId]);


    return (
        <SamContainer>
            {content.map((item, index) => {
                const isSticky = activeNumberId === item.id;
                // Determine if the number should be visible
                const isVisible = (rowPositions[item.id] > -500 && rowPositions[item.id] < 500); // Tweak these values for desired visibility range

                return (
                    <SamRow
                        key={item.id}
                        ref={el => rowRefs.current[index] = el}
                        data-id={item.id}
                        $bgColor={item.color} 
                    >
                        {/* Left Column (Sticky Number) */}
                        <LeftCol 
                            style={{ zIndex: 6 - index }} 
                        >
                            <NumberWrapper 
                                $isSticky={isSticky}
                                $isVisible={isVisible}
                                $yPos={rowPositions[item.id] || 0}
                                zIndex={6 - index}
                            >
                                {item.number}
                            </NumberWrapper>
                        </LeftCol>

                        {/* Right Column (Image) */}
                        <RightCol>
                            <RowImage src={item.image} alt={`Content ${item.number}`} />
                        </RightCol>
                    </SamRow>
                );
            })}
        </SamContainer>
    );
};

export default Sam;
