import React, { useRef, useState, useEffect } from "react";
import illu01 from "./Frame 109.png";
import illu02 from "./Frame 109.png";
import illu03 from "./Frame 109.png";
import illu04 from "./Frame 109.png";
import illu05 from "./Frame 109.png";
import illu06 from "./Frame 109.png";
import signup from "./Frame.png";

const illustrations = [illu01, illu02, illu03, illu04, illu05, illu06];

const content = [
  { number: "01", heading: "Your data stays yours", body: "Your health data is encrypted, never sold, and never shared without your permission." },
  { number: "02", heading: "Built for safety first", body: "AI Doctor avoids harmful or risky recommendations and escalates to clinicians." },
  { number: "03", heading: "Transparent and in your control", body: "You can see why the AI makes decisions. No black boxes." },
  { number: "04", heading: "Medically validated", body: "Every AI Doctor is reviewed by licensed physicians." },
  { number: "05", heading: "Regulation compliant", body: "Complies with HIPAA and medical AI safeguards." },
  { number: "06", heading: "Human doctors oversee everything", body: "Real doctors monitor all cases alongside the AI." }
];

export default function Sam() {
  const containerRef = useRef(null);
  const textRefs = useRef([]);
  const sectionRefs = useRef([]);

  const [lineY, setLineY] = useState(100); // 100vh → bottom

  useEffect(() => {
    const onScroll = () => {
      if (!containerRef.current) return;

      const viewportH = window.innerHeight;
      const container = containerRef.current;
      const total = container.offsetHeight - viewportH;

      const enter = window.scrollY + viewportH - container.offsetTop;

      if (enter <= 0) {
        setLineY(100);
        return;
      }

      const scrolled = window.scrollY - container.offsetTop;
      const progress = Math.max(0, Math.min(scrolled / total, 1));

      setLineY(100 - progress * 100); // continuous smooth movement
    };

    window.addEventListener("scroll", onScroll);
    onScroll();
    return () => window.removeEventListener("scroll", onScroll);
  }, []);

  return (
    <>
      <div className="scroll-carousel" ref={containerRef}>

        {/* LEFT PANEL */}
        <div className="carousel-left">

          {/* ONE GLOBAL LINE */}
          <div className="reveal-line" style={{ top: `${lineY}vh` }} />

          {content.map((item, i) => {
            // smooth hiding animation
            let opacity = 1;
            let translateY = 0;

            const ref = textRefs.current[i];
            if (ref) {
              const rect = ref.getBoundingClientRect();
              const elementCenter = rect.top + rect.height / 2;
              const linePx = (lineY / 100) * window.innerHeight;

              const distance = elementCenter - linePx;

              opacity = Math.max(0, 1 - Math.abs(distance) / 180);
              translateY = -distance * 0.08; // smoother slide
            }

            return (
              <div className="left-block" key={i}>
                <div
                  className="text-wrapper"
                  ref={(el) => (textRefs.current[i] = el)}
                  style={{
                    opacity,
                    transform: `translateY(${translateY}px)`,
                  }}
                >
                  <img src={signup} className="signup-ico" />
                  <h1 className="lef-number">{item.number}</h1>
                  <div className="gradient-border" />
                  <h3 className="lef-title">{item.heading}</h3>
                  <p className="lef-desc">{item.body}</p>
                </div>
              </div>
            );
          })}
        </div>

        {/* RIGHT SCROLL SECTIONS */}
        <div className="carousel-right">
          {illustrations.map((src, i) => (
            <div
              className="content-section"
              key={i}
              ref={(el) => (sectionRefs.current[i] = el)}
            >
              <img src={src} className="illu" />
            </div>
          ))}
        </div>
      </div>

      {/* CSS */}
      <style>{`
        .scroll-carousel {
          display: flex;
          min-height: 600vh;
          width: 100%;
          background: #000;
        }

        .carousel-left {
          width: 40%;
          position: sticky;
          top: 0;
          height: 100vh;
          display: flex;
          justify-content: center;
          background: #000;
        }

        .reveal-line {
          position: absolute;
          width: 100%;
          height: 2px;
          background: white;
          left: 0;
          z-index: 20;
          transition: top 0.06s linear;
        }

        .left-block {
          position: absolute;
          width: 80%;
          height: 100%;
          display: flex;
          justify-content: center;
          align-items: center;
        }

        .text-wrapper {
          width: 100%;
          transition: opacity 0.2s linear, transform 0.2s ease-out;
        }

        .signup-ico { width: 50px; margin-bottom: 20px; }

        .lef-number { font-size: 90px; color: white; font-weight: 300; }

        .gradient-border {
          width: 100%;
          height: 2px;
          background: linear-gradient(to right, #aaa, black);
          margin: 12px 0 18px 0;
        }

        .lef-title {
          font-size: 36px;
          color: white;
          text-transform: uppercase;
        }

        .lef-desc {
          width: 90%;
          font-size: 18px;
          color: #ccc;
        }

        .carousel-right {
          width: 60%;
          border-left: 1px solid #333;
        }

        .content-section {
          height: 100vh;
          display: flex;
          justify-content: center;
          align-items: center;
        }

        .illu {
          width: 75%;
          height: auto;
        }
      `}</style>
    </>
  );
}
