import React, { useRef, useState, useEffect } from "react";
import illu01 from "./Frame 109.png";
import illu02 from "./Frame 109.png";
import illu03 from "./Frame 109.png";
import illu04 from "./Frame 109.png";
import illu05 from "./Frame 109.png";
import illu06 from "./Frame 109.png";
import signup from "./Frame.png";

const illustrations = [illu01, illu02, illu03, illu04, illu05, illu06];

const content = [
  { number: "01", heading: "Your data stays yours", body: "Your health data is encrypted, never sold, and never shared without your permission." },
  { number: "02", heading: "Built for safety first", body: "AI Doctor avoids harmful or risky recommendations and escalates to clinicians." },
  { number: "03", heading: "Transparent and in your control", body: "You can see why the AI makes decisions. No black boxes." },
  { number: "04", heading: "Medically validated", body: "Every AI Doctor is reviewed by licensed physicians." },
  { number: "05", heading: "Regulation compliant", body: "Complies with HIPAA and medical AI safeguards." },
  { number: "06", heading: "Human doctors oversee everything", body: "Real doctors monitor all cases alongside the AI." }
];

export default function Sam() {
  const [activeIndex, setActiveIndex] = useState(0);
  const sectionRefs = useRef([]);

  useEffect(() => {
    const handleScroll = () => {
      const vh = window.innerHeight;

      sectionRefs.current.forEach((ref, i) => {
        if (!ref) return;

        const rect = ref.getBoundingClientRect();
        const inCenter = rect.top <= vh / 2 && rect.bottom >= vh / 2;

        if (inCenter) setActiveIndex(i);
      });
    };

    window.addEventListener("scroll", handleScroll);
    handleScroll();

    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <>
      <div className="scroll-carousel">

        {/* LEFT STICKY PANEL */}
        <div className="carousel-left">

          {/* SINGLE STICKY CARD */}
          <div className="left-sticky-card">

            {/* Number */}
            <h1 className="sticky-number">{content[activeIndex].number}</h1>

            {/* Text */}
            <img src={signup} className="signup-ico" alt="" />
            <div className="gradient-border" />
            <h3 className="lef-title">{content[activeIndex].heading}</h3>
            <p className="lef-desc">{content[activeIndex].body}</p>

          </div>

        </div>

        {/* RIGHT SIDE IMAGES */}
        <div className="carousel-right">
          {content.map((_, i) => (
            <div
              key={i}
              className="content-section"
              ref={(el) => (sectionRefs.current[i] = el)}
            >
              <img src={illustrations[i]} className="illu" alt="" />
            </div>
          ))}
        </div>

      </div>

      {/* CSS */}
      <style>{`
        .scroll-carousel {
          display: flex;
          background: #000;
          min-height: 600vh;
        }

        /* LEFT PANEL */
        .carousel-left {
          width: 40%;
          height: 100vh;
          position: sticky;
          top: 0;
          display: flex;
          justify-content: center;
          align-items: center;
          background: black;
          border-right: 1px solid white;
        }

        /* SINGLE TEXT CARD THAT CHANGES */
        .left-sticky-card {
          width: 90%;
          height: auto;
          color: white;
          transition: opacity 0.4s ease-in-out;
        }

        .sticky-number {
          font-size: 90px;
          font-weight: 300;
          text-align: right;
          margin-right: 10%;
        }

        .signup-ico {
          width: 50px;
          margin-bottom: 20px;
        }

        .gradient-border {
          width: 100%;
          height: 2px;
          background: linear-gradient(to right, #777, #000);
          margin: 20px 0;
        }

        .lef-title {
          font-size: 35px;
          margin-bottom: 10px;
        }

        .lef-desc {
          width: 90%;
          font-size: 18px;
          opacity: 0.8;
        }

        /* RIGHT SIDE */
        .carousel-right {
          width: 60%;
        }

        .content-section {
          height: 100vh;
          display: flex;
          justify-content: center;
          align-items: center;
        }

        .illu {
          width: 75%;
        }
      `}</style>
    </>
  );
}
