import React, { useRef, useState, useEffect } from "react";
import illu01 from "./Frame 109.png";
import illu02 from "./Frame 109.png";
import illu03 from "./Frame 109.png";
import illu04 from "./Frame 109.png";
import illu05 from "./Frame 109.png";
import illu06 from "./Frame 109.png";
import signup from "./Frame.png";

const illustrations = [illu01, illu02, illu03, illu04, illu05, illu06];

const content = [
  { number: "01", heading: "Your data stays yours", body: "Your health data is encrypted, never sold, and never shared without your permission." },
  { number: "02", heading: "Built for safety first", body: "AI Doctor avoids harmful or risky recommendations and escalates to clinicians." },
  { number: "03", heading: "Transparent and in your control", body: "You can see why the AI makes decisions. No black boxes." },
  { number: "04", heading: "Medically validated", body: "Every AI Doctor is reviewed by licensed physicians." },
  { number: "05", heading: "Regulation compliant", body: "Complies with HIPAA and medical AI safeguards." },
  { number: "06", heading: "Human doctors oversee everything", body: "Real doctors monitor all cases alongside the AI." }
];

export default function Sam() {
  const [sections, setSections] = useState([]);
  const sectionRefs = useRef([]);

  /* -----------------------------------------------------------
     PAGE-BY-PAGE SLIDE UP REVEAL ENGINE
  ----------------------------------------------------------- */
  useEffect(() => {
    const handleScroll = () => {
      const scrollY = window.scrollY;
      const pageHeight = window.innerHeight;

      const pageIndex = Math.round(scrollY / pageHeight); // detects active page

      const updated = sectionRefs.current.map((ref, i) => {
        const start = i * pageHeight;
        const end = (i + 1) * pageHeight;

        // progress from 0 → 1
        const progress = Math.min(Math.max((scrollY - start) / pageHeight, 0), 1);

        const linePosition = 100 - progress * 100;
        const isActive = pageIndex === i;

        return { linePosition, isActive, progress };
      });

      setSections(updated);
    };

    window.addEventListener("scroll", handleScroll);
    handleScroll();
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <>
      <div className="scroll-carousel">

        {/* LEFT FIXED PANEL */}
        <div className="carousel-left">
          {content.map((item, index) => {
            const sec = sections[index] || { linePosition: 100, isActive: index === 0 };

            return (
              <div
                key={index}
                className="content-card"
                style={{ opacity: sec.isActive ? 1 : 0 }}
              >
                {/* Reveal line */}
                <div
                  className="reveal-line"
                  style={{ top: `${sec.linePosition}%` }}
                />

                {/* Content */}
                <div className="left-wrap center-left">
                  <img src={signup} className="signup-ico" alt="" />
                  <h1 className="lef-number">{item.number}</h1>
                  <div className="gradient-border" />
                  <h3 className="lef-title">{item.heading}</h3>
                  <p className="lef-desc">{item.body}</p>
                </div>

                {/* Mask */}
                <div
                  className="content-mask"
                  style={{
                    top: `${sec.linePosition}%`,
                    height: `${100 - sec.linePosition}%`
                  }}
                />
              </div>
            );
          })}
        </div>

        {/* RIGHT PANEL — PAGE BY PAGE SLIDE UP */}
        <div className="carousel-right">
          {content.map((_, i) => (
            <div
              className={`content-section ${
                sections[i]?.isActive ? "active-slide" : ""
              }`}
              key={i}
              ref={(el) => (sectionRefs.current[i] = el)}
            >
              <img src={illustrations[i]} className="illu" alt="" />
            </div>
          ))}
        </div>
      </div>

      {/* INLINE CSS */}
      <style>{`
        .scroll-carousel {
          display: flex;
          height: 600vh;
          width: 100%;
          background: #000;
        }

        .carousel-left {
          width: 40%;
          height: 100vh;
          position: sticky;
          top: 0;
          display: flex;
          justify-content: center;
          align-items: center;
          background: #000;
          overflow: hidden;
        }

        .content-card {
          position: absolute;
          width: 90%;
          height: 100%;
          transition: opacity 0.4s ease;
        }

        .reveal-line {
          position: absolute;
          width: 110%;
          height: 2px;
          margin-left: -5%;
          background: #fff;
          z-index: 10;
          transition: top 0.4s linear;
        }

        .content-mask {
          position: absolute;
          left: 0;
          width: 100%;
          background: #000;
          z-index: 9;
          transition: top 0.4s linear, height 0.4s linear;
        }

        .center-left {
          display: flex;
          flex-direction: column;
          justify-content: center;
          height: 100%;
        }

        .signup-ico {
          width: 50px;
          margin-left: 5%;
          margin-bottom: -150px;
        }

        .lef-number {
          font-size: 90px;
          font-weight: 300;
          margin-left: 70%;
          color: white;
        }

        .gradient-border {
          width: 100%;
          height: 2px;
          background: linear-gradient(to right, #777, #000);
          margin: 10px 0 20px 0;
        }

        .lef-title {
          font-size: 36px;
          color: #fff;
          text-transform: uppercase;
        }

        .lef-desc {
          width: 90%;
          font-size: 18px;
          opacity: 0.75;
          color: #ccc;
        }

        .carousel-right {
          width: 60%;
          border-left: 1px solid white;
        }

        /* RIGHT PANEL — SLIDE UP ANIMATION */
        .content-section {
          height: 100vh;
          display: flex;
          justify-content: center;
          align-items: center;
          background: #000;
          
          transform: translateY(100px);
          opacity: 0;
          transition: transform 0.6s ease, opacity 0.6s ease;
        }

        .content-section.active-slide {
          transform: translateY(0px);
          opacity: 1;
        }

        .illu {
          width: 70%;
        }
      `}</style>
    </>
  );
}
