import React from "react";

const rows = [
  { number: "01", image: "https://via.placeholder.com/400x400?text=Image+1" },
  { number: "02", image: "https://via.placeholder.com/400x400?text=Image+2" },
  { number: "03", image: "https://via.placeholder.com/400x400?text=Image+3" },
  { number: "04", image: "https://via.placeholder.com/400x400?text=Image+4" },
  { number: "05", image: "https://via.placeholder.com/400x400?text=Image+5" },
  { number: "06", image: "https://via.placeholder.com/400x400?text=Image+6" },
];

const Sam = () => {
  return (
    <div className="container">
      <style>{`
        .container {
          width: 100%;
          height: 600vh;
        }

        .row {
          display: flex;
          height: 100vh;
          width: 100%;
        }

        .left {
          width: 40%;
          display: flex;
          justify-content: flex-end;
          align-items: center;
          position: relative;
          border:1px solid white;
        }

        .number {
          font-size: 4rem;
          font-weight: bold;
          position: sticky;
          top: 50%;
          transform: translateY(-50%);
        }

        .right {
          width: 60%;
          display: flex;
          justify-content: center;
          align-items: center;
        }

        .right img {
          max-width: 80%;
          max-height: 80%;
          object-fit: contain;
        }
      `}</style>

      {rows.map((row, index) => (
        <div className="row" key={index}>
          <div className="left" style={{ zIndex: index + 1 }}>
            <div className="number">{row.number}</div>
          </div>
          <div className="right">
            <img src={row.image} alt={`Row ${row.number}`} />
          </div>
        </div>
      ))}
    </div>
  );
};

export default Sam;
