import React, { useEffect, useRef } from "react";
import { gsap } from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";

gsap.registerPlugin(ScrollTrigger);

export default function Sam() {
  const sectionsRef = useRef([]);
  const leftRefs = useRef([]);
  const lineRefs = useRef([]);
  const containerRef = useRef(null);

  const content = [
    { title: "Creative Strategy", body: "Brand direction, storytelling, and deep discovery insights." },
    { title: "Design Execution", body: "Stunning visuals crafted for modern digital experiences." },
    { title: "Motion & Interaction", body: "Smooth transitions and immersive micro-interactions." },
    { title: "Development", body: "High-performance websites with scalable architecture." },
    { title: "Launch & Support", body: "Optimization, testing, deployment and ongoing support." }
  ];

  useEffect(() => {
    const ctx = gsap.context(() => {

      sectionsRef.current.forEach((sec, i) => {
        if (!sec) return;

        /** RIGHT SIDE SLIDE ANIMATION (bottom → center → top) */
        gsap.fromTo(
          sec,
          { y: 150, opacity: 0.3 },
          {
            y: 0,
            opacity: 1,
            duration: 1,
            ease: "power3.out",
            scrollTrigger: {
              trigger: sec,
              start: "top center",
              end: "bottom center",
              scrub: true,
            },
          }
        );

        /** LEFT SIDE TEXT FADE IN + FADE OUT SYNC */
        gsap.fromTo(
          leftRefs.current[i],
          { opacity: 0, y: 40 },
          {
            opacity: 1,
            y: 0,
            duration: 0.8,
            ease: "power2.out",
            scrollTrigger: {
              trigger: sec,
              start: "top center",
              end: "bottom center",
              scrub: true,
            },
          }
        );

        /** LEFT SIDE LINE ERASE EFFECT */
        gsap.fromTo(
          lineRefs.current[i],
          { scaleX: 1 },
          {
            scaleX: 0, // line erases from left-to-right
            transformOrigin: "left center",
            ease: "none",
            scrollTrigger: {
              trigger: sec,
              start: "top center",
              end: "bottom center",
              scrub: true,
            },
          }
        );
      });

    }, containerRef);

    return () => ctx.revert();
  }, []);

  return (
    <>
      <div className="main-container" ref={containerRef}>

        {/* LEFT PANEL */}
        <div className="left-panel">
          {content.map((item, i) => (
            <div key={i} className="left-block" ref={(el) => (leftRefs.current[i] = el)}>
              <div className="left-line" ref={(el) => (lineRefs.current[i] = el)}></div>
              <h1 className="left-number">{String(i + 1).padStart(2, "0")}</h1>
              <h2 className="left-title">{item.title}</h2>
              <p className="left-desc">{item.body}</p>
            </div>
          ))}
        </div>

        {/* RIGHT PANEL */}
        <div className="right-panel">
          {content.map((item, i) => (
            <div
              className="section"
              key={i}
              ref={(el) => (sectionsRef.current[i] = el)}
            >
              <div className="box">{item.title}</div>
            </div>
          ))}
        </div>
      </div>

      {/* INLINE CSS */}
      <style>
        {`
          body { margin: 0; background: #000; color: white; font-family: Arial; }

          .main-container {
            display: flex;
            height: auto;
          }

          .left-panel {
            width: 40%;
            position: sticky;
            top: 0;
            height: 100vh;
            padding: 40px;
            border-right: 1px solid #333;
          }

          .left-block {
            position: absolute;
            opacity: 0;
          }

          .left-number {
            font-size: 80px;
            opacity: 0.3;
            margin: 0;
          }

          .left-title {
            margin: 5px 0;
            font-size: 32px;
            text-transform: uppercase;
          }

          .left-desc {
            font-size: 20px;
            opacity: 0.75;
            width: 90%;
            line-height: 1.5;
          }

          .left-line {
            width: 150px;
            height: 3px;
            background: white;
            margin-bottom: 20px;
            transform-origin: left center;
          }

          .right-panel {
            width: 60%;
          }

          .section {
            height: 100vh;
            display: flex;
            justify-content: center;
            align-items: center;
            opacity: 0.3;
          }

          .box {
            width: 65%;
            height: 50%;
            border: 2px solid white;
            display: flex;
            justify-content: center;
            align-items: center;
            font-size: 40px;
            text-transform: uppercase;
          }
        `}
      </style>
    </>
  );
}
