import React, { useRef, useState, useEffect } from "react";
// Assuming image imports work based on local file structure
import signup from "./Frame.png"; 
import illu01 from "./Frame 109.png";
import illu02 from "./Frame 109.png";
import illu03 from "./Frame 109.png";
import illu04 from "./Frame 109.png";
import illu05 from "./Frame 109.png";
import illu06 from "./Frame 109.png";

const illustrations = [illu01, illu02, illu03, illu04, illu05, illu06];

const content = [
  { number: "01", heading: "Your data stays yours ", body: "Your health data is encrypted, never sold, and never shared without your permission." },
  { number: "02", heading: "Built for safety first", body: "AI Doctor avoids harmful or risky recommendations and escalates to clinicians." },
  { number: "03", heading: "Transparent and in your control", body: "You can see why the AI makes decisions. No black boxes." },
  { number: "04", heading: "Medically validated", body: "Every AI Doctor is reviewed by licensed physicians." },
  { number: "05", heading: "Regulation compliant", body: "Complies with HIPAA and medical AI safeguards." },
  { number: "06", heading: "Human doctors oversee everything", body: "Real doctors monitor all cases alongside the AI." }
];

// --- CSS AS A JAVASCRIPT STRING ---
const styles = `
/* Basic Reset and Setup */
body {
    margin: 0;
    padding: 0;
    background-color: #000; 
    color: #fff;
    font-family: 'Arial', sans-serif;
    min-height: 100vh; 
}

/* Main Container for the entire layout */
.scroll-carousel {
    display: flex;
    width: 100%;
}

/* LEFT COLUMN: Fixed Number and Subtitle */
.carousel-left {
    position: sticky; 
    top: 0; 
    width: 40%;
    height: 100vh; 
    padding-left: 5%; 
    display: flex;
    align-items: center; 
    z-index: 1;
    background-color: #000;
    position: relative; 
}

/* Reveal Line - Smoothed transition for fast scroll */
.reveal-line {
    position: absolute;
    left: 5%; 
    width: 35%; 
    height: 2px;
    background: linear-gradient(to right, #fff, #666, #000);
    z-index: 3;
    /* FIX: Slower and smoother transition for stability on fast scroll */
    transition: top 0.2s cubic-bezier(0.19, 1, 0.22, 1); 
    pointer-events: none;
}

/* Content Mask - Follows the line with a smoothed transition */
.content-mask {
    position: absolute;
    left: 0;
    width: 100%;
    background-color: #000;
    z-index: 2;
    pointer-events: none;
    /* FIX: Match transition duration with the line */
    transition: top 0.2s cubic-bezier(0.19, 1, 0.22, 1), height 0.2s cubic-bezier(0.19, 1, 0.22, 1);
}

/* Styling for the fixed number and text */
.fixed-index-display {
    text-align: left;
    position: relative;
    z-index: 1;
    opacity: 1;
    /* Animation triggered by the 'key={activeItem}' prop change */
    animation: slideIn 0.6s ease forwards;
}

@keyframes slideIn {
    0% {
        opacity: 0;
        transform: translateY(40px);
    }
    100% {
        opacity: 1;
        transform: translateY(0px);
    }
}
        
/* Styling for specific elements */
.signup-ico {
  width: 48px;
  margin-bottom: 20px;
}
.lef-number {
  font-size: 100px;
  font-weight: 300;
  margin: 0;
  font-family: Raleway;
}
.lef-title {
  font-size: 38px;
  font-weight: 300;
  text-transform: uppercase;
  margin-top: 20px;
  margin-bottom: 20px;
  padding-top: 30px;
  border-top: 2px solid;
  border-image: linear-gradient(to right, rgb(92, 91, 91), transparent) 1;
}
.lef-desc {
  font-size: 20px;
  opacity: 0.7;
  width: 90%;
  line-height: 1.5;
  margin-top: 12px;
  font-family: Raleway;
}


/* RIGHT COLUMN: Scrollable Content */
.carousel-right {
    width: 60%;
    z-index: 5;
    border-left: 1px solid rgb(192, 192, 192);
    background-color: #000;
}

/* Individual Content Sections (The Triggers) */
.content-section {
    height: 100vh; 
    display: flex;
    flex-direction: column;
    justify-content: center; 
    align-items: center; 
    background-color: #000;
}

.illu {
    max-width: 80%;
    max-height: 80vh;
    object-fit: contain;
}
`;
// --- END CSS STRING ---


export default function Sam() {
  const [scrollProgress, setScrollProgress] = useState(0);
  const [activeItem, setActiveItem] = useState(0);
  
  const containerRef = useRef(null);
  const sectionRefs = useRef([]);

  // EFFECT TO INJECT CSS STYLES INTO THE HEAD
  useEffect(() => {
    const styleTag = document.createElement('style');
    styleTag.innerHTML = styles;
    document.head.appendChild(styleTag);
    
    // Cleanup: remove the style tag when the component unmounts
    return () => {
      document.head.removeChild(styleTag);
    };
  }, []); // Run only once on mount

  useEffect(() => {
    
    // Handle global scroll to calculate progress (for line movement)
    const handleScroll = () => {
      const scrollTop = window.scrollY;
      const docHeight = document.documentElement.scrollHeight - window.innerHeight;
      const progress = docHeight > 0 ? scrollTop / docHeight : 0;
      setScrollProgress(progress);
    };

    // SECTION OBSERVER (Triggers content change when section is centered)
    const sectionObserver = new IntersectionObserver(
      entries => {
        entries.forEach(entry => {
          if (entry.isIntersecting) {
            const index = Number(entry.target.dataset.index);
            if (!isNaN(index)) {
              setActiveItem(index);
            }
          }
        });
      },
      // rootMargin: "-50% 0px -50% 0px" triggers when the center of the element hits the center of the viewport
      { rootMargin: "-50% 0px -50% 0px" } 
    );

    sectionRefs.current.forEach(ref => {
      if (ref) sectionObserver.observe(ref);
    });
    
    window.addEventListener('scroll', handleScroll);

    // Clean-up function
    return () => {
      window.removeEventListener('scroll', handleScroll);
      sectionRefs.current.forEach(ref => ref && sectionObserver.unobserve(ref));
    };
  }, []);

  // Clamp the line position to ensure stability
  const rawLinePosition = 100 - (scrollProgress * 100 * content.length);
  // Ensures the line stays between 0vh (top) and 100vh (bottom)
  const clampedLinePosition = Math.min(100, Math.max(0, rawLinePosition));

  return (
    <div className="scroll-carousel" ref={containerRef}>

      {/* LEFT STICKY CONTENT */}
      <div className="carousel-left">
         {/* Reveal Line: Uses clamped position and CSS transition for smoothness */}
         <div 
          className="reveal-line" 
          style={{ top: `${clampedLinePosition}vh` }}
        />
        
        {/* Fixed Content: Key forces re-render/animation on activeItem change */}
        <div
          className="fixed-index-display" 
          key={activeItem}
        >
          <img src={signup} className="signup-ico" alt="signup" />
          <h1 className="lef-number">{content[activeItem].number}</h1>
          <h3 className="lef-title">{content[activeItem].heading}</h3>
          <p className="lef-desc">{content[activeItem].body}</p>
        </div>
        
        {/* Overlay Mask: Follows the line, creating the erasing/revealing effect */}
        <div 
          className="content-mask" 
          style={{ 
            top: `${clampedLinePosition}vh`,
            height: `${100 - clampedLinePosition}vh`
          }}
        />
      </div>

      {/* RIGHT SCROLL CONTENT */}
      <div className="carousel-right">
        {content.map((_, index) => (
          <div
            key={index}
            className="content-section"
            ref={el => (sectionRefs.current[index] = el)}
            data-index={index}
          >
            <img src={illustrations[index]} className="illu" alt={`Illustration ${index + 1}`} />
          </div>
        ))}
      </div>

    </div>
  );
}
