// Sam.js (Updated with LEFT numbers + fixed initial position)
import React, { useRef, useEffect, useState } from 'react';
import styled, { css } from 'styled-components';

const ROW_COLORS = [
    '#0e0d0d',
    '#250c0c',
    '#0c251e',
    '#25240c',
    '#0c1525',
    '#250c25',
];

const dummyImage = "https://via.placeholder.com/600x800.png?text=Image";
const images = [
  `${dummyImage}+01`,
  `${dummyImage}+02`,
  `${dummyImage}+03`,
  `${dummyImage}+04`,
  `${dummyImage}+05`,
  `${dummyImage}+06`,
];

const content = [
  { id: 1, number: '01', image: images[0], color: ROW_COLORS[0] },
  { id: 2, number: '02', image: images[1], color: ROW_COLORS[1] },
  { id: 3, number: '03', image: images[2], color: ROW_COLORS[2] },
  { id: 4, number: '04', image: images[3], color: ROW_COLORS[3] },
  { id: 5, number: '05', image: images[4], color: ROW_COLORS[4] },
  { id: 6, number: '06', image: images[5], color: ROW_COLORS[5] },
];

const SamContainer = styled.div`
  width: 100%;
  background: black;
  color: white;
`;

const SamRow = styled.div`
  display: flex;
  height: 100vh;
  width: 100%;
  background-color: ${(p) => p.$bgColor};
`;

const LeftCol = styled.div`
  flex: 0 0 40%;
  border-top: 1px solid white;
  border-bottom: 1px solid white;
  position: relative;
  overflow: hidden;
`;

const RightCol = styled.div`
  flex: 0 0 60%;
  border-top: 1px solid white;
  border-bottom: 1px solid white;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const RowImage = styled.img`
  width: 70%;
  height: auto;
  object-fit: contain;
`;

// --- Number animation ---
const NumberWrapper = styled.div`
  position: absolute;
  right: 10%;
  font-size: 7rem;
  font-weight: 700;

  /* Hidden initially (inside the row, not outside page) */
  opacity: 0;
  transform: translateY(120px);

  transition: opacity 0.35s ease-out, transform 0.35s ease-out;

  ${(props) =>
    props.$isActive &&
    css`
      opacity: 1;
      transform: translateY(0px);
      position: sticky;
      top: 50%;
      transform: translateY(-50%);
    `}
`;

const Sam = () => {
  const rowRefs = useRef([]);
  const [activeRow, setActiveRow] = useState(1);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          const id = parseInt(entry.target.dataset.id);

          if (entry.isIntersecting) {
            setActiveRow(id);
          }
        });
      },
      {
        root: null,
        rootMargin: "-40% 0px -40% 0px",
        threshold: 0,
      }
    );

    rowRefs.current.forEach((row) => {
      if (row) observer.observe(row);
    });

    return () => observer.disconnect();
  }, []);

  return (
    <SamContainer>
      {content.map((item, index) => (
        <SamRow
          key={item.id}
          data-id={item.id}
          ref={(el) => (rowRefs.current[index] = el)}
          $bgColor={item.color}
          style={{ zIndex: 6 - index }}
        >
          {/* LEFT COLUMN NUMBER */}
          <LeftCol>
            <NumberWrapper $isActive={activeRow === item.id}>
              {item.number}
            </NumberWrapper>
          </LeftCol>

          {/* RIGHT COLUMN IMAGE */}
          <RightCol>
            <RowImage src={item.image} alt="img" />
          </RightCol>
        </SamRow>
      ))}
    </SamContainer>
  );
};

export default Sam;
