import React, { useEffect, useRef } from "react";

export default function Sam() {
  const rowRefs = useRef([]);
  const numberRefs = useRef([]);

  useEffect(() => {
    const handleScroll = () => {
      const viewportCenter = window.innerHeight / 2;

      rowRefs.current.forEach((row, index) => {
        const rect = row.getBoundingClientRect();
        const number = numberRefs.current[index];

        if (!row || !number) return;

        const rowCenter = rect.top + rect.height / 2;

        // Position logic — number moves from bottom → center but never above center
        if (rect.top > viewportCenter) {
          number.style.transform = "translateY(80px)"; // still below
          number.style.opacity = "0";
        } 
        else if (rect.bottom < viewportCenter) {
          number.style.transform = "translateY(-80px)"; 
          number.style.opacity = "0"; // hidden by next row border
        } 
        else {
          number.style.transform = "translateY(0px)";
          number.style.opacity = "1"; // visible at center
        }
      });
    };

    window.addEventListener("scroll", handleScroll);
    handleScroll();
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const numbers = ["01", "02", "03", "04", "05", "06"];

  return (
    <>
      <div className="sam-container">
        {numbers.map((num, i) => (
          <div
            key={i}
            className="sam-row"
            ref={(el) => (rowRefs.current[i] = el)}
            style={{ zIndex: i + 1 }}
          >
            {/* LEFT COLUMN */}
            <div className="sam-left">
              <div
                className="sam-number"
                ref={(el) => (numberRefs.current[i] = el)}
              >
                {num}
              </div>
            </div>

            {/* RIGHT COLUMN */}
            <div className="sam-right">
              <img
                src={`https://picsum.photos/800/800?random=${i + 1}`}
                alt=""
                className="sam-image"
              />
            </div>
          </div>
        ))}
      </div>

      {/* INLINE CSS */}
      <style>{`
        .sam-container {
          width: 100%;
          height: 600vh;
          background: #000;
        }

        .sam-row {
          display: flex;
          height: 100vh;
          width: 100%;
          position: relative;
        }

        /* LEFT COLUMN */
        .sam-left {
          width: 40%;
          border-top: 1px solid white;
          border-bottom: 1px solid white;
          position: relative;
          display: flex;
          justify-content: center;
          align-items: center;
        }

        .sam-number {
          position: sticky;
          top: 50%;
          transform: translateY(80px);
          font-size: 120px;
          font-weight: 400;
          color: white;
          opacity: 0;
          transition: transform 0.4s ease, opacity 0.4s ease;
          z-index: inherit;
        }

        /* RIGHT COLUMN */
        .sam-right {
          width: 60%;
          border-top: 1px solid white;
          border-bottom: 1px solid white;
          display: flex;
          justify-content: center;
          align-items: center;
        }

        .sam-image {
          width: 70%;
          height: auto;
          object-fit: cover;
          border: 1px solid white;
        }
      `}</style>
    </>
  );
}
