import React, { useRef, useState, useEffect } from "react";

// Placeholder images
const signup = "data:image/svg+xml,%3Csvg width='48' height='48' xmlns='http://www.w3.org/2000/svg'%3E%3Crect width='48' height='48' fill='%23666'/%3E%3C/svg%3E";
const illu = "data:image/svg+xml,%3Csvg width='400' height='300' xmlns='http://www.w3.org/2000/svg'%3E%3Crect width='400' height='300' fill='%23333'/%3E%3Ctext x='50%25' y='50%25' text-anchor='middle' fill='white' font-size='24'%3EIllustration%3C/text%3E%3C/svg%3E";

const illustrations = [illu, illu, illu, illu, illu, illu];

const content = [
  { number: "01", heading: "Your data stays yours", body: "Your health data is encrypted, never sold, and never shared without your permission." },
  { number: "02", heading: "Built for safety first", body: "AI Doctor avoids harmful or risky recommendations and escalates to clinicians." },
  { number: "03", heading: "Transparent and in your control", body: "You can see why the AI makes decisions. No black boxes." },
  { number: "04", heading: "Medically validated", body: "Every AI Doctor is reviewed by licensed physicians." },
  { number: "05", heading: "Regulation compliant", body: "Complies with HIPAA and medical AI safeguards." },
  { number: "06", heading: "Human doctors oversee everything", body: "Real doctors monitor all cases alongside the AI." }
];

export default function ScrollCarousel() {
  const [sections, setSections] = useState([]);
  const containerRef = useRef(null);
  const sectionRefs = useRef([]);

  useEffect(() => {
    const handleScroll = () => {
      const viewportHeight = window.innerHeight;

      const updated = sectionRefs.current.map((ref, index) => {
        if (!ref) return { linePosition: 100, isActive: false };

        const rect = ref.getBoundingClientRect();

        // Calculate progress: 0 when section bottom hits viewport bottom, 1 when section top hits viewport top
        const progress = Math.min(
          Math.max((viewportHeight - rect.top) / viewportHeight, 0),
          1
        );

        // Line travels from 100% (bottom) to 0% (top)
        const linePosition = 100 - progress * 100;

        // Active when section center is near viewport center
        const isActive =
          rect.top <= viewportHeight / 2 && rect.bottom >= viewportHeight / 2;

        return { linePosition, isActive };
      });

      setSections(updated);
    };

    window.addEventListener("scroll", handleScroll);
    handleScroll();

    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <>
      <div className="scroll-carousel" ref={containerRef}>
        {/* LEFT STICKY PANEL */}
        <div className="carousel-left">
          {content.map((item, index) => {
            const sec = sections[index] || {
              linePosition: 100,
              isActive: index === 0
            };

            return (
              <div
                key={index}
                className="content-card"
                style={{ opacity: sec.isActive ? 1 : 0.3 }}
              >
                {/* Reveal line */}
                <div
                  className="reveal-line"
                  style={{ top: `${sec.linePosition}%` }}
                />

                {/* Content */}
                <div className="left-wrap">
                  <img src={signup} className="signup-ico" alt="" />
                  <h1 className="lef-number">{item.number}</h1>
                  <div className="gradient-border" />
                  <h3 className="lef-title">{item.heading}</h3>
                  <p className="lef-desc">{item.body}</p>
                </div>

                {/* Mask - hides content below the line */}
                <div
                  className="content-mask"
                  style={{
                    top: `${sec.linePosition}%`,
                    height: `${100 - sec.linePosition}%`
                  }}
                />
              </div>
            );
          })}
        </div>

        {/* RIGHT SIDE */}
        <div className="carousel-right">
          {content.map((_, i) => (
            <div
              className="content-section"
              key={i}
              ref={(el) => (sectionRefs.current[i] = el)}
            >
              <img src={illustrations[i]} className="illu" alt="" />
            </div>
          ))}
        </div>
      </div>

      {/* INLINE CSS */}
      <style>{`
        * {
          margin: 0;
          padding: 0;
          box-sizing: border-box;
        }

        body {
          background-color: #000;
          color: #fff;
          font-family: 'Raleway', 'Arial', sans-serif;
        }

        .scroll-carousel {
          display: flex;
          min-height: 600vh;
          background: #000;
          width: 100%;
          position: relative;
        }

        .carousel-left {
          width: 40%;
          height: 100vh;
          position: sticky;
          top: 0;
          display: flex;
          justify-content: center;
          align-items: center;
          background: #000;
          overflow: hidden;
          padding-left: 5%;
        }

        .content-card {
          position: absolute;
          width: 100%;
          height: 100%;
          transition: opacity 0.5s ease;
          display: flex;
          align-items: center;
        }

        .reveal-line {
          position: absolute;
          left: -5%;
          width: 110%;
          height: 2px;
          background: linear-gradient(to right, #fff, #666, #000);
          z-index: 10;
          transition: top 0.05s linear;
          pointer-events: none;
        }

        .content-mask {
          position: absolute;
          left: 0;
          width: 100%;
          background: #000;
          z-index: 9;
          transition: top 0.05s linear, height 0.05s linear;
          pointer-events: none;
        }

        .left-wrap {
          position: relative;
          z-index: 1;
          width: 90%;
        }

        .signup-ico {
          width: 48px;
          position: absolute;
          margin-top: -50px;
          margin-left: 0;
        }

        .lef-number {
          font-size: 94px;
          font-weight: 300;
          margin-left: 300px;
          margin-top: -80px;
          color: white;
          font-family: Raleway, Arial, sans-serif;
          line-height: 1;
        }

        .gradient-border {
          width: 100%;
          height: 2px;
          background: linear-gradient(to right, rgb(92, 91, 91), #000);
          margin: 30px 0 20px 0;
        }

        .lef-title {
          font-size: 38px;
          font-weight: 300;
          text-transform: uppercase;
          color: #fff;
          margin-bottom: 20px;
          font-family: Raleway, Arial, sans-serif;
        }

        .lef-desc {
          width: 90%;
          font-size: 20px;
          opacity: 0.7;
          color: #ccc;
          line-height: 1.5;
          font-family: Raleway, Arial, sans-serif;
        }

        .carousel-right {
          width: 60%;
          border-left: 1px solid rgb(192, 192, 192);
          background: #000;
        }

        .content-section {
          height: 100vh;
          display: flex;
          justify-content: center;
          align-items: center;
          background: #000;
        }

        .illu {
          max-width: 75%;
          height: auto;
        }

        @media (max-width: 700px) {
          .carousel-left {
            width: 100%;
            padding-left: 5%;
          }
          
          .carousel-right {
            display: none;
          }
          
          .lef-number {
            margin-left: 150px;
            font-size: 60px;
          }
          
          .lef-title {
            font-size: 24px;
          }
          
          .lef-desc {
            font-size: 16px;
          }
        }
      `}</style>
    </>
  );
}
