import React, { useRef, useState, useEffect } from "react";

const illustrations = [
  'https://images.unsplash.com/photo-1506905925346-21bda4d32df4?w=800&h=600&fit=crop',
  'https://images.unsplash.com/photo-1519681393784-d120267933ba?w=800&h=600&fit=crop',
  'https://images.unsplash.com/photo-1506929562872-bb421503ef21?w=800&h=600&fit=crop',
  'https://images.unsplash.com/photo-1511593358241-7eea1f3c84e5?w=800&h=600&fit=crop',
  'https://images.unsplash.com/photo-1475924156734-496f6cac6ec1?w=800&h=600&fit=crop',
  'https://images.unsplash.com/photo-1501594907352-04cda38ebc29?w=800&h=600&fit=crop'
];

const content = [
  { number: "01" },
  { number: "02" },
  { number: "03" },
  { number: "04" },
  { number: "05" },
  { number: "06" }
];

export default function Sam() {
  const [sections, setSections] = useState([]);
  const sectionRefs = useRef([]);

  useEffect(() => {
    const handleScroll = () => {
      const viewportHeight = window.innerHeight;

      const updated = sectionRefs.current.map((ref) => {
        if (!ref) return { isActive: false };

        const rect = ref.getBoundingClientRect();

        // ACTIVE when section starts entering from bottom
        const isActive = rect.top <= viewportHeight;

        return { isActive };
      });

      setSections(updated);
    };

    window.addEventListener("scroll", handleScroll);
    handleScroll();

    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <>
      <div className="scroll-carousel">

        {/* LEFT FIXED PANEL */}
        <div className="carousel-left">
          {content.map((item, index) => {
            const sec = sections[index] || { isActive: index === 0 };

            return (
              <div
                key={index}
                className="content-card"
                style={{
                  opacity: sec.isActive ? 1 : 0,
                  zIndex: index + 1,
                  backgroundColor: "black",
                  borderTop: "1px solid white",
                  borderBottom: "1px solid white",
                  // Come from bottom to center, stick at center
                  transform: sec.isActive ? "translateY(0)" : "translateY(100vh)",
                  transition: "transform 1.2s ease-out, opacity 0.8s ease-out"
                }}
              >
                <div className="number-container">
                  <h1 className="lef-number">{item.number}</h1>
                </div>
              </div>
            );
          })}
        </div>

        {/* RIGHT SIDE IMAGES */}
        <div className="carousel-right">
          {content.map((_, i) => (
            <div
              className="content-section"
              key={i}
              ref={(el) => (sectionRefs.current[i] = el)}
            >
              <img src={illustrations[i]} className="illu" alt={`Illustration ${i + 1}`} />
            </div>
          ))}
        </div>

      </div>

      {/* CSS */}
      <style>{`
        * {
          margin: 0;
          padding: 0;
          box-sizing: border-box;
        }

        body {
          background: black;
        }

        .scroll-carousel {
          display: flex;
          min-height: 600vh;
          background: #000;
          width: 100%;
        }

        .carousel-left {
          width: 40%;
          height: 100vh;
          position: sticky;
          top: 0;
          display: flex;
          justify-content: center;
          align-items: center;
          overflow: hidden;
        }

        .content-card {
          position: absolute;
          width: 100%;
          height: 100vh;
          display: flex;
          align-items: center;
          justify-content: flex-end;
          padding-right: 3rem;
        }

        .number-container {
          position: sticky;
          top: 50%;
          transform: translateY(-50%);
        }

        .lef-number {
          font-size: 8rem;
          font-weight: 700;
          color: white;
          margin: 0;
          line-height: 1;
        }

        .carousel-right {
          width: 60%;
        }

        .content-section {
          height: 100vh;
          display: flex;
          justify-content: center;
          align-items: center;
          border-left: 1px solid white;
        }

        .illu {
          max-width: 80%;
          max-height: 80vh;
          object-fit: contain;
        }
      `}</style>
    </>
  );
}
