import { useEffect, useState, useRef } from "react";
import "./Box7.css";
import img from "./b4image.png";

function Box7() {
  const [activeIndex, setActiveIndex] = useState(0);

  const headingsRef = useRef([]);

  useEffect(() => {
    const options = { threshold: 0.6 };

    const observer = new IntersectionObserver((entries) => {
      entries.forEach((entry) => {
        if (entry.isIntersecting) {
          const index = Number(entry.target.getAttribute("data-index"));
          setActiveIndex(index);
        }
      });
    }, options);

    headingsRef.current.forEach((el) => observer.observe(el));

    return () => observer.disconnect();
  }, []);

  return (
    <div className="box7-container">
      <div className="flw1">
        <p className="pa1">No Surprises. No Fine Print. Just the Truth.</p>
        <p className="pa2">
          We believe in complete transparency. Here's exactly what you get with
          Qronic AI, what your insurance already covers, and what's optional.
        </p>
      </div>

      {/* ---------- HEADINGS ---------- */}
      <div className="flw2">
        {["INCLUDED WITH CHRONIC AI", "COVERED BY INSURANCE", "USER CHOICE"].map(
          (text, i) => (
            <p
              key={i}
              data-index={i}
              ref={(el) => (headingsRef.current[i] = el)}
              className={activeIndex === i ? "active-heading" : ""}
            >
              {text}
            </p>
          )
        )}
      </div>

      {/* ---------- CONTENT ---------- */}
      <div className="cont">
        {activeIndex === 0 && (
          <div className="c1">
            <img className="b4image" src={img} alt="" />
            <p>Hello</p>
          </div>
        )}

        {activeIndex === 1 && (
          <div className="c2">
            <img className="b4image" src={img} alt="" />
            <p>HII</p>
          </div>
        )}

        {activeIndex === 2 && (
          <div className="c3">
            <img className="b4image" src={img} alt="" />
            <p>Buddy</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default Box7;
