import React, { useRef, useState, useEffect } from "react";
import illu01 from "./Frame 109.png";
import illu02 from "./Frame 109.png";
import illu03 from "./Frame 109.png";
import illu04 from "./Frame 109.png";
import illu05 from "./Frame 109.png";
import illu06 from "./Frame 109.png";
import signup from "./Frame.png";

const illustrations = [illu01, illu02, illu03, illu04, illu05, illu06];

const content = [
  { number: "01", heading: "Your data stays yours", body: "Your health data is encrypted, never sold, and never shared without your permission." },
  { number: "02", heading: "Built for safety first", body: "AI Doctor avoids harmful or risky recommendations and escalates to clinicians." },
  { number: "03", heading: "Transparent and in your control", body: "You can see why the AI makes decisions. No black boxes." },
  { number: "04", heading: "Medically validated", body: "Every AI Doctor is reviewed by licensed physicians." },
  { number: "05", heading: "Regulation compliant", body: "Complies with HIPAA and medical AI safeguards." },
  { number: "06", heading: "Human doctors oversee everything", body: "Real doctors monitor all cases alongside the AI." }
];

export default function Sam() {
  const [sections, setSections] = useState([]);
  const [activeIndex, setActiveIndex] = useState(0);
  const sectionRefs = useRef([]);

  useEffect(() => {
    const handleScroll = () => {
      const vh = window.innerHeight;
      let newActive = 0;

      const updated = sectionRefs.current.map((ref, index) => {
        if (!ref) return { linePosition: 100, progress: 0 };

        const rect = ref.getBoundingClientRect();

        // Row progress: 0 → entering, 1 → fully passed
        const progress = Math.min(Math.max((vh - rect.top) / vh, 0), 1);
        const linePosition = 100 - progress * 100;

        // Active row detection
        if (rect.top <= vh / 2 && rect.bottom >= vh / 2) {
          newActive = index;
        }

        return { linePosition, progress };
      });

      setSections(updated);
      setActiveIndex(newActive);
    };

    window.addEventListener("scroll", handleScroll);
    handleScroll();
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  // Retrieve active section data
  const sec = sections[activeIndex] || { linePosition: 100, progress: 0 };
  const item = content[activeIndex];

  // Smooth page-up / page-down effect
  const slideY = (1 - sec.progress) * 40; // 40px vertical motion

  return (
    <>
      <div className="scroll-carousel">

        {/* LEFT FIXED PANEL */}
        <div className="carousel-left">

          <div
            className="content-card"
            style={{
              transform: `translateY(${slideY}px)`,
              opacity: 1,
              transition: "transform 0.35s ease-out"
            }}
          >
            {/* Line */}
            <div className="reveal-line" style={{ top: `${sec.linePosition}%` }} />

            {/* Centered Left Content */}
            <div className="left-wrap center-left">
              <img src={signup} className="signup-ico" alt="" />
              <h1 className="lef-number">{item.number}</h1>
              <div className="gradient-border" />
              <h3 className="lef-title">{item.heading}</h3>
              <p className="lef-desc">{item.body}</p>
            </div>

            {/* Mask */}
            <div
              className="content-mask"
              style={{
                top: `${sec.linePosition}%`,
                height: `${100 - sec.linePosition}%`
              }}
            />
          </div>

        </div>

        {/* RIGHT SECTIONS */}
        <div className="carousel-right">
          {content.map((_, i) => (
            <div
              className="content-section"
              key={i}
              ref={(el) => (sectionRefs.current[i] = el)}
            >
              <img src={illustrations[i]} className="illu" alt="" />
            </div>
          ))}
        </div>

      </div>

      {/* CSS */}
      <style>{`
        .scroll-carousel {
          display: flex;
          min-height: 600vh;
          background: #000;
          width: 100%;
        }

        .carousel-left {
          width: 40%;
          height: 100vh;
          position: sticky;
          top: 0;
          display: flex;
          justify-content: center;
          align-items: center;
          background: #000;
          overflow: hidden;
        }

        .content-card {
          position: absolute;
          width: 90%;
          height: 100%;
        }

        .reveal-line {
          position: absolute;
          height: 2px;
          width: 110%;
          margin-left: -5%;
          background: #fff;
          z-index: 10;
          transition: top 0.35s linear;
        }

        .content-mask {
          position: absolute;
          width: 100%;
          background: #000;
          transition: top 0.35s linear, height 0.35s linear;
          z-index: 9;
        }

        .center-left {
          height: 100%;
          display: flex;
          flex-direction: column;
          justify-content: center;
        }

        .signup-ico { width: 50px; margin-bottom: -150px; margin-left: 5%; }

        .lef-number { font-size: 90px; margin-left: 70%; color: white; }

        .gradient-border {
          height: 2px;
          background: linear-gradient(to right, #777, #000);
          margin: 15px 0;
        }

        .lef-title {
          color: white;
          font-size: 36px;
          text-transform: uppercase;
        }

        .lef-desc {
          width: 90%;
          opacity: 0.75;
          color: #ccc;
          font-size: 18px;
        }

        .carousel-right {
          width: 60%;
          border-left: 1px solid white;
        }

        .content-section {
          height: 100vh;
          display: flex;
          justify-content: center;
          align-items: center;
        }

        .illu {
          width: 75%;
        }
      `}</style>
    </>
  );
}
