import React, { useRef, useState, useEffect } from "react";
import illu01 from "./Frame 109.png";
import illu02 from "./Frame 109.png";
import illu03 from "./Frame 109.png";
import illu04 from "./Frame 109.png";
import illu05 from "./Frame 109.png";
import illu06 from "./Frame 109.png";
import signup from "./Frame.png";

const illustrations = [illu01, illu02, illu03, illu04, illu05, illu06];

const content = [
  { number: "01", heading: "Your data stays yours", body: "Your health data is encrypted, never sold, and never shared without your permission." },
  { number: "02", heading: "Built for safety first", body: "AI Doctor avoids harmful or risky recommendations and escalates to clinicians." },
  { number: "03", heading: "Transparent and in your control", body: "You can see why the AI makes decisions. No black boxes." },
  { number: "04", heading: "Medically validated", body: "Every AI Doctor is reviewed by licensed physicians." },
  { number: "05", heading: "Regulation compliant", body: "Complies with HIPAA and medical AI safeguards." },
  { number: "06", heading: "Human doctors oversee everything", body: "Real doctors monitor all cases alongside the AI." }
];

export default function Sam() {
  const [sections, setSections] = useState([]);
  const sectionRefs = useRef([]);

  useEffect(() => {
    const handleScroll = () => {
      const vh = window.innerHeight;

      const updated = sectionRefs.current.map((ref) => {
        if (!ref) return { linePosition: 100 };

        const rect = ref.getBoundingClientRect();

        // Progress mapping: section enters bottom → 0, exiting top → 1
        const progress = Math.min(
          Math.max((vh - rect.top) / vh, 0),
          1
        );

        const linePosition = 100 - progress * 100;

        return { linePosition };
      });

      setSections(updated);
    };

    window.addEventListener("scroll", handleScroll);
    handleScroll();

    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <>
      <div className="scroll-carousel">

        {/* LEFT FIXED PANEL */}
        <div className="carousel-left">
          {content.map((item, index) => {
            const sec = sections[index] || { linePosition: 100 };

            // hide only after line touches the content
            const hideStart = 40; 
            const opacity = Math.max(0, (sec.linePosition - hideStart) / 60);

            return (
              <div key={index} className="content-card" style={{ opacity }}>
                
                {/* Line */}
                <div className="reveal-line" style={{ top: `${sec.linePosition}%` }} />

                {/* Left content */}
                <div className="left-wrap center-left">
                  <img src={signup} className="signup-ico" />
                  <h1 className="lef-number">{item.number}</h1>
                  <div className="gradient-border" />
                  <h3 className="lef-title">{item.heading}</h3>
                  <p className="lef-desc">{item.body}</p>
                </div>

                {/* Mask below line */}
                <div
                  className="content-mask"
                  style={{
                    top: `${sec.linePosition}%`,
                    height: `${100 - sec.linePosition}%`
                  }}
                />
              </div>
            );
          })}
        </div>

        {/* RIGHT SIDE */}
        <div className="carousel-right">
          {illustrations.map((img, idx) => (
            <div
              className="content-section"
              key={idx}
              ref={(el) => (sectionRefs.current[idx] = el)}
            >
              <img src={img} className="illu" />
            </div>
          ))}

          {/* ⭐ Add more images to extend scrolling smoothly */}
          {illustrations.map((img, idx) => (
            <div
              className="content-section"
              key={"extra-" + idx}
            >
              <img src={img} className="illu" />
            </div>
          ))}
        </div>

      </div>

      {/* CSS */}
      <style>{`
        .scroll-carousel {
          display: flex;
          min-height: 900vh; /* extended height */
          background: #000;
          width: 100%;
        }

        .carousel-left {
          width: 40%;
          height: 100vh;
          position: sticky;
          top: 0;
          display: flex;
          justify-content: center;
          align-items: center;
          background: #000;
        }

        .content-card {
          position: absolute;
          width: 90%;
          height: 100%;
          transition: opacity 0.35s linear;
        }

        .reveal-line {
          position: absolute;
          width: 110%;
          margin-left: -5%;
          height: 2px;
          background: #fff;
          transition: top 0.35s linear;
          z-index: 10;
        }

        .content-mask {
          position: absolute;
          width: 100%;
          background: #000;
          transition: top 0.35s linear, height 0.35s linear;
          z-index: 9;
        }

        .center-left {
          height: 100%;
          display: flex;
          flex-direction: column;
          justify-content: center;
        }

        .signup-ico { width: 50px; margin-left: 5%; margin-bottom: -150px; }

        .lef-number { font-size: 90px; color: #fff; margin-left: 70%; }

        .gradient-border {
          height: 2px;
          width: 100%;
          background: linear-gradient(to right, #777, #000);
        }

        .lef-title { font-size: 36px; color: white; }

        .lef-desc { width: 90%; opacity: 0.75; color: #ccc; }

        .carousel-right {
          width: 60%;
          border-left: 1px solid white;
        }

        .content-section {
          height: 100vh;
          display: flex;
          justify-content: center;
          align-items: center;
          background: black;
        }

        .illu {
          width: 75%;
        }
      `}</style>
    </>
  );
}
