import { useEffect, useRef, useState } from "react";
import "./ss.css";

import desktopImg from "./b4image.png";

import i1 from "./i1.png";
import i2 from "./i2.png";
import i3 from "./i3.png";
import i4 from "./i4.png";
import i5 from "./i1.png";
import i6 from "./i1.png";

export default function SS() {
  const stageRef = useRef(null);
  const cardsRef = useRef([]);
  const [isMobile, setIsMobile] = useState(window.innerWidth <= 768);

  useEffect(() => {
    function onResize() {
      setIsMobile(window.innerWidth <= 768);
    }
    window.addEventListener("resize", onResize);
    return () => window.removeEventListener("resize", onResize);
  }, []);

  useEffect(() => {
    if (isMobile) return;

    function updateActive() {
      const stage = stageRef.current;
      if (!stage) return;

      const rect = stage.getBoundingClientRect();
      const scrollHeight = stage.offsetHeight - window.innerHeight;
      const progress = Math.min(Math.max(-rect.top / scrollHeight, 0), 1);
      const index = Math.round(progress * 5);

      cardsRef.current.forEach((card, i) => {
        if (i === index) card.classList.add("active");
        else card.classList.remove("active");
      });
    }

    window.addEventListener("scroll", updateActive);
    updateActive();
    return () => window.removeEventListener("scroll", updateActive);
  }, [isMobile]);

  const mobileImages = [i1, i2, i3, i4, i5, i6];

  const steps = [
    "We get to know your health story",
    "You create your own AI Doctor",
    "Your devices connect automatically",
    "Your AI Doctor builds a digital model of your body",
    "You receive daily individualized guidance",
    "A clinician supervises everything",
  ];

  const descriptions = [
    "Share your health history, goals and what matters most to you.",
    "You create a personalized digital health model.",
    "All your devices sync effortlessly.",
    "We build your internal biology model.",
    "Your guidance updates every day.",
    "A clinician supervises everything.",
  ];

  return (
    <section className="ss-stage" ref={stageRef}>
      <div className="ss-wrapper">

        {steps.map((text, i) => (
          <div key={i} className="ss-card" ref={(el) => (cardsRef.current[i] = el)}>

            {/* DESKTOP ELEMENTS */}
            <img
              src={desktopImg}
              alt="visual"
              className="desktop-img"
            />
            <p className="desktop-title">{text}</p>
            <span className="desktop-number">{String(i + 1).padStart(2, "0")}</span>
            <p className="po desktop-po">{descriptions[i]}</p>

            {/* MOBILE ELEMENTS */}
            <div className="mobile-row">
              <div className="mobile-left">
                <span className="mobile-step">{String(i + 1).padStart(2, "0")}</span>
              </div>

              <div
                className="mobile-right"
                style={{ backgroundImage: `url(${mobileImages[i]})` }}
              >
                <div className="mobile-content">
                  <h3>{text}</h3>
                  <p>{descriptions[i]}</p>
                </div>
              </div>
            </div>

          </div>
        ))}

      </div>
    </section>
  );
}
