import React, { useRef, useState, useEffect } from "react";

// Placeholder images
const signup = "data:image/svg+xml,%3Csvg width='48' height='48' xmlns='http://www.w3.org/2000/svg'%3E%3Crect width='48' height='48' fill='%23666'/%3E%3C/svg%3E";
const illu = "data:image/svg+xml,%3Csvg width='400' height='300' xmlns='http://www.w3.org/2000/svg'%3E%3Crect width='400' height='300' fill='%23333'/%3E%3Ctext x='50%25' y='50%25' text-anchor='middle' fill='white' font-size='24'%3EIllustration%3C/text%3E%3C/svg%3E";

const illustrations = [illu, illu, illu, illu, illu, illu];

const content = [
  { number: "01", heading: "Your data stays yours", body: "Your health data is encrypted, never sold, and never shared without your permission." },
  { number: "02", heading: "Built for safety first", body: "AI Doctor avoids harmful or risky recommendations and escalates to clinicians." },
  { number: "03", heading: "Transparent and in your control", body: "You can see why the AI makes decisions. No black boxes." },
  { number: "04", heading: "Medically validated", body: "Every AI Doctor is reviewed by licensed physicians." },
  { number: "05", heading: "Regulation compliant", body: "Complies with HIPAA and medical AI safeguards." },
  { number: "06", heading: "Human doctors oversee everything", body: "Real doctors monitor all cases alongside the AI." }
];

export default function Sam() {
  const [activeItem, setActiveItem] = useState(0);
  const [scrollProgress, setScrollProgress] = useState(0);
  const containerRef = useRef(null);
  const sectionRefs = useRef([]);

  useEffect(() => {
    const handleScroll = () => {
      if (!containerRef.current) return;
      
      const scrollTop = window.scrollY;
      const docHeight = document.documentElement.scrollHeight - window.innerHeight;
      const progress = scrollTop / docHeight;
      setScrollProgress(progress);
    };

    const sectionObserver = new IntersectionObserver(
      entries => {
        entries.forEach(entry => {
          if (entry.isIntersecting) {
            const index = Number(entry.target.dataset.index);
            if (!isNaN(index)) {
              setActiveItem(index);
            }
          }
        });
      },
      { rootMargin: "-50% 0px -50% 0px" }
    );

    sectionRefs.current.forEach(ref => {
      if (ref) sectionObserver.observe(ref);
    });

    window.addEventListener('scroll', handleScroll);
    handleScroll();

    return () => {
      window.removeEventListener('scroll', handleScroll);
      sectionRefs.current.forEach(ref => ref && sectionObserver.unobserve(ref));
    };
  }, []);

  // Calculate line position - starts at bottom (100vh) and moves to top (0vh)
  const linePosition = 100 - (scrollProgress * 100 * content.length);

  return (
    <div className="scroll-carousel" ref={containerRef}>
      {/* LEFT STICKY CONTENT */}
      <div className="carousel-left">
        {/* Horizontal reveal line */}
        <div 
          className="reveal-line" 
          style={{ top: `${linePosition}vh` }}
        />
        
        <div className="fixed-index-display" key={activeItem}>
          <img src={signup} className="signup-ico" alt="signup" />
          <h1 className="lef-number">{content[activeItem].number}</h1>
          <h3 className="lef-title gradient-border-top">{content[activeItem].heading}</h3>
          <p className="lef-desc">{content[activeItem].body}</p>
        </div>
        
        {/* Overlay mask - hides content below the line */}
        <div 
          className="content-mask" 
          style={{ 
            top: `${linePosition}vh`,
            height: `${100 - linePosition}vh`
          }}
        />
      </div>

      {/* RIGHT SCROLL CONTENT */}
      <div className="carousel-right">
        {content.map((item, index) => (
          <React.Fragment key={index}>
            <div
              className="content-section"
              ref={el => (sectionRefs.current[index] = el)}
              data-index={index}
            >
              <img src={illustrations[index]} className="illu" alt="" />
            </div>
            {index < content.length - 1 && (
              <div className="section-divider">
                <div className="divider-line" />
                <div className="divider-content">
                  <span className="divider-number">{item.number}</span>
                  <span className="divider-arrow">â†’</span>
                  <span className="divider-number">{content[index + 1].number}</span>
                </div>
                <div className="divider-line" />
              </div>
            )}
          </React.Fragment>
        ))}
      </div>

      <style>{`
        * {
          margin: 0;
          padding: 0;
          box-sizing: border-box;
        }

        body {
          background-color: #000;
          color: #fff;
          font-family: 'Raleway', 'Arial', sans-serif;
        }

        .scroll-carousel {
          display: flex;
          min-height: ${content.length * 120}vh;
          width: 100%;
          position: relative;
        }

        .carousel-left {
          position: fixed;
          top: 0;
          left: 0;
          width: 40%;
          height: 100vh;
          padding-left: 5%;
          display: flex;
          align-items: center;
          z-index: 10;
          background-color: #000;
          overflow: hidden;
        }

        .reveal-line {
          position: absolute;
          left: 0;
          width: 100%;
          height: 2px;
          background: linear-gradient(to right, #fff, #666, #000);
          z-index: 3;
          transition: top 0.1s linear;
          pointer-events: none;
        }

        .content-mask {
          position: absolute;
          left: 0;
          width: 100%;
          background-color: #000;
          z-index: 2;
          pointer-events: none;
          transition: top 0.1s linear, height 0.1s linear;
        }

        .fixed-index-display {
          text-align: left;
          position: relative;
          z-index: 1;
          opacity: 1;
          animation: slideUp 0.6s ease forwards;
        }

        @keyframes slideUp {
          0% {
            opacity: 0;
            transform: translateY(40px);
          }
          100% {
            opacity: 1;
            transform: translateY(0px);
          }
        }

        .lef-number {
          font-size: 94px;
          font-weight: 300;
          margin-left: 300px;
          margin-top: -80px;
          font-family: Raleway, Arial, sans-serif;
          line-height: 1;
        }

        .lef-title {
          font-size: 38px;
          font-weight: 300;
          text-transform: uppercase;
          text-align: left;
          color: white;
          margin-bottom: 20px;
          padding-top: 30px;
        }

        .gradient-border-top {
          position: relative;
        }

        .gradient-border-top::before {
          content: "";
          position: absolute;
          top: 0;
          left: 0;
          width: 100%;
          height: 2px;
          background: linear-gradient(to right, rgb(92, 91, 91), black);
        }

        .lef-desc {
          font-size: 20px;
          opacity: 0.7;
          width: 90%;
          line-height: 1.5;
          margin-top: 12px;
          text-align: left;
          font-family: Raleway, Arial, sans-serif;
          font-weight: 400;
        }

        .signup-ico {
          width: 48px;
          position: absolute;
          margin-top: -50px;
        }

        .carousel-right {
          margin-left: 40%;
          width: 60%;
          border-left: 1px solid rgb(192, 192, 192);
          background-color: #000;
        }

        .content-section {
          height: 100vh;
          display: flex;
          flex-direction: column;
          justify-content: center;
          align-items: center;
          background-color: #000;
        }

        .illu {
          max-width: 80%;
          height: auto;
        }

        .section-divider {
          height: 20vh;
          display: flex;
          align-items: center;
          justify-content: center;
          gap: 30px;
          padding: 0 60px;
          background-color: #000;
        }

        .divider-line {
          flex: 1;
          height: 1px;
          background: linear-gradient(to right, transparent, #666, transparent);
        }

        .divider-content {
          display: flex;
          align-items: center;
          gap: 20px;
          color: #666;
          font-size: 24px;
          font-weight: 300;
          font-family: Raleway, Arial, sans-serif;
        }

        .divider-number {
          font-size: 32px;
          color: #999;
        }

        .divider-arrow {
          color: #666;
          font-size: 28px;
        }
      `}</style>
    </div>
  );
}
