import React, { useRef, useState, useEffect } from "react";
import illu01 from "./Frame 109.png";
import illu02 from "./Frame 109.png";
import illu03 from "./Frame 109.png";
import illu04 from "./Frame 109.png";
import illu05 from "./Frame 109.png";
import illu06 from "./Frame 109.png";
import signup from "./Frame.png";

const illustrations = [illu01, illu02, illu03, illu04, illu05, illu06];

const bgColors = ["black","black","black","black","black","black"];
const borderColors = ["white","white","white","white","white","white"];

const content = [
  { number: "01", heading: "Your data stays yours", body: "Your health data is encrypted, never sold, and never shared without your permission." },
  { number: "02", heading: "Built for safety first", body: "AI Doctor avoids harmful or risky recommendations and escalates to clinicians." },
  { number: "03", heading: "Transparent and in your control", body: "You can see why the AI makes decisions. No black boxes." },
  { number: "04", heading: "Medically validated", body: "Every AI Doctor is reviewed by licensed physicians." },
  { number: "05", heading: "Regulation compliant", body: "Complies with HIPAA and medical AI safeguards." },
  { number: "06", heading: "Human doctors oversee everything", body: "Real doctors monitor all cases alongside the AI." }
];

export default function Sam() {
  const [sections, setSections] = useState([]);
  const sectionRefs = useRef([]);

  useEffect(() => {
    const handleScroll = () => {
      const viewportHeight = window.innerHeight;

      const updated = sectionRefs.current.map(ref => {
        if (!ref) return { isActive: false };

        const rect = ref.getBoundingClientRect();
        const isActive = rect.top <= viewportHeight / 2 && rect.bottom >= viewportHeight / 2;

        return { isActive };
      });

      setSections(updated);
    };

    window.addEventListener("scroll", handleScroll);
    handleScroll();
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const activeIndex = sections.findIndex(s => s.isActive);
  const fallbackIndex = activeIndex === -1 ? 0 : activeIndex;

  return (
    <>
      <div className="scroll-carousel">

        {/* LEFT FIXED PANEL */}
        <div className="carousel-left">
          {content.map((item, index) => {
            const sec = sections[index] || { isActive: index === 0 };
            const isActive = sec.isActive;

            const isPast = index < fallbackIndex;
            const isFuture = index > fallbackIndex;

            let transformValue = "translateY(0)";
            if (isFuture) transformValue = "translateY(100vh)";
            if (isPast) transformValue = "translateY(-100vh)";

            return (
              <div
                key={index}
                className="content-card"
                style={{
                  opacity: isActive ? 1 : 0,
                  zIndex: index,
                  backgroundColor: bgColors[index],
                  border: `1px solid ${borderColors[index]}`,
                  transform: transformValue,
                  transition: "transform 1s ease-in-out, opacity 0.5s ease-in-out"
                }}
              >
                <div className="left-wrap center-left">
                  <img src={signup} className="signup-ico" alt="" />
                  <h1 className="lef-number">{item.number}</h1>
                  <div className="gradient-border" />
                  <h3 className="lef-title">{item.heading}</h3>
                  <p className="lef-desc">{item.body}</p>
                </div>
              </div>
            );
          })}
        </div>

        {/* RIGHT SIDE IMAGES */}
        <div className="carousel-right">
          {illustrations.map((img, index) => (
            <div
              key={index}
              className="content-section"
              ref={(el) => (sectionRefs.current[index] = el)}
            >
              <img src={img} className="illu" alt="" />
            </div>
          ))}
        </div>
      </div>

      {/* CSS */}
      <style>{`
        .scroll-carousel {
          display: flex;
          min-height: 600vh;
          background: #000;
          width: 98%;
          border: 1px solid white;
          margin: 20px auto;
        }

        .carousel-left {
          width: 40%;
          height: 100vh;
          position: sticky;
          top: 0;
          display: flex;
          justify-content: center;
          align-items: flex-start;
          overflow: hidden;
        }

        .content-card {
          position: absolute;
          width: 100%;
          height: 100%;
          padding: 0px;
          margin: 0px;
          box-sizing: border-box;
          text-align: left;
        }

        .center-left {
          display: flex;
          flex-direction: column;
          justify-content: flex-start;   /* <-- FIXED CONTENT AT TOP */
          padding-top: 40px;             /* <-- ADJUST GAP */
          height: 100%;
        }

        .signup-ico {
          width: 50px;
          margin-left: 5%;
          margin-bottom: 20px;
        }

        .lef-number {
          font-size: 90px;
          font-weight: 300;
          margin-left: 70%;
          color: white;
        }

        .gradient-border {
          width: 100%;
          height: 2px;
          background: linear-gradient(to right, #777, #000);
          margin: 10px 0 20px 0;
        }

        .lef-title {
          font-size: 36px;
          color: white;
          text-transform: uppercase;
        }

        .lef-desc {
          width: 90%;
          font-size: 18px;
          opacity: 0.75;
          color: #ccc;
        }

        .carousel-right {
          width: 60%;
          border-left: 1px solid white;
        }

        .content-section {
          height: 100vh;
          display: flex;
          justify-content: center;
          align-items: center;
        }

        .illu {
          width: 75%;
        }
      `}</style>
    </>
  );
}
