import { useEffect, useState } from "react";
import "./hiw.css";
import img from "./b4image.png";

function Hiw() {
  const [activeIndex, setActiveIndex] = useState(0);

  useEffect(() => {
    const onScroll = () => {
      const scrollY = window.scrollY;
      const maxScroll = window.innerHeight * 2; // adjust sensitivity

      const index = Math.min(
        2,
        Math.floor((scrollY / maxScroll) * 3)
      );

      setActiveIndex(index);
    };

    window.addEventListener("scroll", onScroll);
    return () => window.removeEventListener("scroll", onScroll);
  }, []);

  return (
    <div className="hiw-container">

      {/* LEFT (always visible) */}
      <div className="lft">
        <p className={activeIndex === 0 ? "active" : ""}>
          01 Your AI Doctor studies the way your body behaves
        </p>
        <p className={activeIndex === 1 ? "active" : ""}>
          02 Behind every insight is real clinical reasoning.
        </p>
        <p className={activeIndex === 2 ? "active" : ""}>
          03 Clear explanations. Simple actions. No jargon.
        </p>
      </div>

      {/* RIGHT (changes only) */}
      <div className="rgt">
        <img
          src={img}
          alt="step 1"
          className={`hiwimage ${activeIndex === 0 ? "show" : ""}`}
        />
        <img
          src={img}
          alt="step 2"
          className={`hiwimage ${activeIndex === 1 ? "show" : ""}`}
        />
        <img
          src={img}
          alt="step 3"
          className={`hiwimage ${activeIndex === 2 ? "show" : ""}`}
        />
      </div>

    </div>
  );
}

export default Hiw;
