// Sam.js (Modified to ensure '01' is sticky upon load)
import React, { useRef, useEffect, useState } from 'react';
import styled, { css } from 'styled-components';

// --- 1. CONFIGURATION AND DATA (Same as previous, defining colors) ---
const ROW_COLORS = [
    '#0e0d0d', '#250c0c', '#0c251e', 
    '#25240c', '#0c1525', '#250c25',
];

const dummyImage = "https://via.placeholder.com/600x800.png?text=Image";
const images = [ /* ... image array definitions ... */ ]; // Keeping this brief for clarity

const content = [
  { id: 1, number: '01', image: `${dummyImage}+01`, color: ROW_COLORS[0] },
  { id: 2, number: '02', image: `${dummyImage}+02`, color: ROW_COLORS[1] },
  { id: 3, number: '03', image: `${dummyImage}+03`, color: ROW_COLORS[2] },
  { id: 4, number: '04', image: `${dummyImage}+04`, color: ROW_COLORS[3] },
  { id: 5, number: '05', image: `${dummyImage}+05`, color: ROW_COLORS[4] },
  { id: 6, number: '06', image: `${dummyImage}+06`, color: ROW_COLORS[5] },
];

// --- 2. STYLED COMPONENTS (CSS-in-JS) ---

// (GlobalStyle, SamContainer, SamCol, RightCol, RowImage remain the same)

const GlobalStyle = css` /* ... CSS content ... */ `;
const SamContainer = styled.div` /* ... CSS content ... */ `;
const SamRow = styled.div`
  /* ... Previous CSS ... */
  background-color: ${props => props.$bgColor || 'black'}; 
`;
const SamCol = styled.div` /* ... CSS content ... */ `;
const LeftCol = styled(SamCol)`
  /* ... Previous CSS ... */
  border-top: 1px solid white;
  border-bottom: 1px solid white; 
`;
const RightCol = styled(SamCol)` /* ... CSS content ... */ `;
const RowImage = styled.img` /* ... CSS content ... */ `;


// Number Wrapper (CRITICAL CHANGE HERE)
const NumberWrapper = styled.div`
  /* Default state: positioned for the scroll effect */
  position: absolute; 
  top: 50%;
  right: 5%; 
  
  font-size: 5rem;
  font-weight: bold;
  color: #444; 
  transition: color 0.3s ease;
  
  /* Initial state: Hidden below the screen center (Req. 11) */
  opacity: 0;
  transform: translate(0, 100vh); 

  /* Active/Sticky State */
  ${props => props.$isSticky && css`
    position: fixed; 
    top: 50%;
    right: 5%; 
    transform: translateY(-50%);
    
    color: white; 
    opacity: 1;
    
    transition: opacity 0.3s, color 0.3s;
  `}
`;

// --- 3. React Component Logic (CRITICAL CHANGE HERE) ---

const Sam = () => {
  // CRITICAL CHANGE 1: Initialize activeNumberId to 1.
  const [activeNumberId, setActiveNumberId] = useState(1);
  const rowRefs = useRef([]);
  const hasCheckedInitialVisibility = useRef(false);

  useEffect(() => {
    const observerOptions = {
      root: null, 
      rootMargin: '-45% 0px -45% 0px', 
      threshold: 0, 
    };

    const observer = new IntersectionObserver((entries) => {
      entries.forEach(entry => {
        const rowId = parseInt(entry.target.dataset.id);
        
        if (entry.isIntersecting) {
          // If a row is intersecting the center, make it active
          setActiveNumberId(rowId);
        } 
      });
    }, observerOptions);

    // --- CRITICAL CHANGE 2: Manually check visibility on mount ---
    // If the component is placed last, the Observer might fire immediately.
    // If the first row is above the rootMargin, we manually set it to active 
    // to ensure '01' is sticky until the next row scrolls in.
    if (!hasCheckedInitialVisibility.current && rowRefs.current[0]) {
        const firstRow = rowRefs.current[0];
        const rect = firstRow.getBoundingClientRect();
        
        // Check if the top of the first row is currently visible on screen
        if (rect.top < window.innerHeight && rect.bottom > 0) {
            setActiveNumberId(1);
        } else {
             // If the first row is entirely off-screen (e.g., component is high up),
             // reset to a default state, allowing the observer to take over naturally.
             // We keep it at 1 here to enforce the '01 always visible' requirement.
             setActiveNumberId(1);
        }
        hasCheckedInitialVisibility.current = true;
    }


    rowRefs.current.forEach(ref => {
      if (ref) observer.observe(ref);
    });

    return () => {
      rowRefs.current.forEach(ref => {
        if (ref) observer.unobserve(ref);
      });
    };
  }, []); // Empty dependency array ensures this runs only once on mount

  return (
    <SamContainer>
      {content.map((item, index) => (
        <SamRow
          key={item.id}
          ref={el => rowRefs.current[index] = el}
          data-id={item.id}
          $bgColor={item.color} 
        >
          {/* Left Column (Sticky Number) */}
          <LeftCol 
            style={{ zIndex: 6 - index }} 
          >
            {/* The '01' number will be sticky because activeNumberId is initialized to 1 */}
            <NumberWrapper 
                $isSticky={activeNumberId === item.id}
            >
                {item.number}
            </NumberWrapper>
          </LeftCol>

          {/* Right Column (Image) */}
          <RightCol>
            <RowImage src={item.image} alt={`Content ${item.number}`} />
          </RightCol>
        </SamRow>
      ))}
    </SamContainer>
  );
};

export default Sam;
