import React from 'react';

// Import your images here. Replace these with your actual image paths.
// For demonstration, I'm using placeholder names.
import image01 from './iPhone.png';
import image02 from './iPhone.png';
import image03 from './iPhone.png';
import image04 from './iPhone.png';
import image05 from './iPhone.png';
import image06 from './iPhone.png';

const data = [
  { number: '01', image: image01 },
  { number: '02', image: image02 },
  { number: '03', image: image03 },
  { number: '04', image: image04 },
  { number: '05', image: image05 },
  { number: '06', image: image06 },
];

/* ⚠️ IMPORTANT: As noted before, 'position: sticky' requires dedicated CSS or a CSS-in-JS solution. 
  This revised solution embeds the necessary CSS via a <style> tag to ensure the sticky behavior works 
  inside this single file.
*/

// --- CORRECTED CSS (Embedded in JS) ---
const styles = `
.sam-container {
  background-color: black;
  color: white;
}

/* 1) The row container is 100vh tall and uses flex for the 40%/60% split.
  8) Borders applied to the row.
*/
.sam-row {
  display: flex;
  height: 100vh; 
  border-bottom: 1px solid white; 
}
.sam-row:first-child {
  border-top: 1px solid white; 
}

/* Base style for columns */
.sam-col {
  display: flex;
  align-items: center; /* Vertical center alignment */
  justify-content: center; /* Horizontal center alignment */
}

/* 1) Column width: 40% : 60% */
.sam-left-wrap {
  width: 40%; 
  border-right: 1px solid white; 
  overflow: hidden;
}

.sam-right-wrap {
  width: 60%;
}

/* 4) Image centered in the right column */
.sam-image {
  max-width: 90%;
  max-height: 90%;
  object-fit: contain;
}

/* --- STICKY CORRECTION ---
  To make the number stick at the center of the screen height (50vh) for the duration 
  of a 100vh scroll:
  1. The direct parent of the sticky element needs to be tall (e.g., 200vh) to provide 
     scroll distance.
  2. The sticky element uses 'top: 50%' and 'transform: translateY(-50%)'.
*/
.sam-sticky-number-container {
  height: 200vh; /* Provides the necessary scroll distance for the sticky effect to last 100vh */
  width: 100%;
}

.sam-number-wrapper {
  position: sticky; /* 5) Key for the sticky effect */
  top: 50%;         /* 5) Sticks the top edge of the wrapper at the vertical center of the screen */
  transform: translateY(-50%); /* 5) Moves the wrapper up by half its own height to truly center it */
  height: auto; /* Content height */
  width: 100%;
  
  display: flex;
  justify-content: flex-end; /* 3) Center right positioning */
  align-items: center;
  padding-right: 10%; /* Adjusted padding for 'center right' look */
}

.sam-number {
  font-size: 5rem;
  font-weight: bold;
}
`;
// -----------------------------------------------------------

const Sam = () => {
  return (
    <>
      <style>{styles}</style>
      
      <div className="sam-container">
        {data.map((item, index) => (
          <div key={index} className="sam-row">
            
            {/* 40% Left Column Wrapper */}
            <div className="sam-left-wrap">
              {/* This container is 200vh high and holds the sticky element. 
                This height is critical for the 'sticky at center' effect to work 
                across the entire 100vh row scroll.
              */}
              <div className="sam-sticky-number-container">
                <div 
                  className="sam-number-wrapper" 
                  // 6) Z-index: Higher for newer items to overlap previous ones when scrolling down.
                  style={{ zIndex: data.length - index }} 
                >
                  <div className="sam-number">{item.number}</div>
                </div>
              </div>
            </div>

            {/* 60% Right Column Wrapper */}
            <div className="sam-right-wrap sam-col">
              <img src={item.image} alt={`Content ${item.number}`} className="sam-image" />
            </div>
          </div>
        ))}
      </div>
    </>
  );
};

export default Sam;
