import { useEffect, useState, useRef } from "react";
import "./hiw.css";
import i1 from "./i1.png";
import i2 from "./i2.png";
import i3 from "./i3.png";

function Hiw() {
  const [activeIndex, setActiveIndex] = useState(0);
  const wrapperRef = useRef(null);

  useEffect(() => {
    const handleScroll = () => {
      if (!wrapperRef.current) return;

      const rect = wrapperRef.current.getBoundingClientRect();
      const scrollProgress = -rect.top; // How much of the wrapper has scrolled past top
      const wrapperHeight = rect.height - window.innerHeight;
      
      // Calculate index based on 3 steps
      let progress = scrollProgress / wrapperHeight;
      let index = Math.floor(progress * 3);
      
      // Constraints
      if (index < 0) index = 0;
      if (index > 2) index = 2;

      setActiveIndex(index);
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <div className="hiw-wrapper" ref={wrapperRef}>
      <div className="hiw-container">
        
        {/* LEFT (Stays in view, text highlights) */}
        <div className="lft">
            <p>
          <p className={activeIndex === 0 ? "active" : ""}>
           Your AI Doctor studies the way your body behaves
          </p>
          </p>
          
          <p className={activeIndex === 1 ? "active" : ""}>
            02 Behind every insight is real clinical reasoning.
          </p>
          <p className={activeIndex === 2 ? "active" : ""}>
            03 Clear explanations. Simple actions. No jargon.
          </p>
        </div>

        {/* RIGHT (Images swap in place) */}
        <div className="rgt">
          <img src={i1} className={`hiwimage ${activeIndex === 0 ? "show" : ""}`} alt="Step 1" />
          <img src={i2} className={`hiwimage ${activeIndex === 1 ? "show" : ""}`} alt="Step 2" />
          <img src={i3} className={`hiwimage ${activeIndex === 2 ? "show" : ""}`} alt="Step 3" />
        </div>

      </div>
    </div>
  );
}

export default Hiw;
