// Sam.js
import React, { useRef, useEffect, useState } from 'react';
import styled, { css } from 'styled-components';

// --- 1. CONFIGURATION AND DATA ---

// Define different colors for each row (For better visual separation)
const ROW_COLORS = [
    '#0e0d0d', // 01: Dark Black (Base)
    '#250c0c', // 02: Dark Red/Brown
    '#0c251e', // 03: Dark Teal/Green
    '#25240c', // 04: Dark Yellow/Mustard
    '#0c1525', // 05: Dark Blue
    '#250c25', // 06: Dark Purple
];

// Dummy Image URLs (Replace with your actual image imports)
const dummyImage = "https://via.placeholder.com/600x800.png?text=Image";
const content = [
  { id: 1, number: '01', image: `${dummyImage}+01`, color: ROW_COLORS[0] },
  { id: 2, number: '02', image: `${dummyImage}+02`, color: ROW_COLORS[1] },
  { id: 3, number: '03', image: `${dummyImage}+03`, color: ROW_COLORS[2] },
  { id: 4, number: '04', image: `${dummyImage}+04`, color: ROW_COLORS[3] },
  { id: 5, number: '05', image: `${dummyImage}+05`, color: ROW_COLORS[4] },
  { id: 6, number: '06', image: `${dummyImage}+06`, color: ROW_COLORS[5] },
];

// --- 2. STYLED COMPONENTS (CSS-in-JS) ---

// Global body styles (Req. 9)
const GlobalStyle = css`
  body {
    background-color: ${ROW_COLORS[0]}; 
    color: white;
    margin: 0;
    padding: 0;
  }
`;

// Main Container
const SamContainer = styled.div`
  ${GlobalStyle}
  display: block;
  width: 100%;
`;

// Row Styling (Req. 2 & New Color Req)
const SamRow = styled.div`
  display: flex;
  height: 100vh; /* Req. 2 */
  width: 100%;
  background-color: ${props => props.$bgColor || 'black'}; 
`;

// Column Base Styling
const SamCol = styled.div`
  display: flex; 
  align-items: center;
  justify-content: center;
`;

// Left Column (Req. 1, 8, 10, 11, 12)
const LeftCol = styled(SamCol)`
  flex: 0 0 40%; /* Req. 1 */
  position: relative; 
  /* CRUCIAL for Req. 10, 11, 12: Hides the number that goes above the top border */
  overflow: hidden; 
  /* Req. 8 & 9 */
  border-top: 1px solid white;
  border-bottom: 1px solid white; 
`;

// Right Column (Req. 1, 7)
const RightCol = styled(SamCol)`
  flex: 0 0 60%; /* Req. 1 */
`;

// Image Styling (Req. 4)
const RowImage = styled.img`
  max-width: 80%;
  max-height: 80%;
  object-fit: contain;
`;

// Number Wrapper (Req. 3, 5, 11)
const NumberWrapper = styled.div`
  /* Default state: positioned for the scroll effect */
  position: absolute; 
  top: 50%;
  right: 5%; /* Req. 5: At center right */
  
  font-size: 5rem;
  font-weight: bold;
  color: #444; /* Dim number when not active */
  transition: color 0.3s ease;
  
  /* Initial state: Hidden below the screen center (Req. 11) */
  opacity: 0;
  transform: translate(0, 100vh); 

  /* Active/Sticky State (Req. 5) */
  ${props => props.$isSticky && css`
    /* Sets position fixed relative to the viewport (sticky) */
    position: fixed; 
    top: 50%;
    right: 5%; 
    transform: translateY(-50%);
    
    color: white; /* Highlight active number */
    opacity: 1;
    
    /* Disabling transform transition to make it immediately 'fixed' */
    transition: opacity 0.3s, color 0.3s;
  `}
`;

// --- 3. React Component Logic ---

const Sam = () => {
  // Initialize activeNumberId to 1, assuming the user starts at the top
  const [activeNumberId, setActiveNumberId] = useState(1);
  const rowRefs = useRef([]);

  useEffect(() => {
    // Intersection Observer Configuration
    const observerOptions = {
      root: null, // Viewport
      // Target an area covering the middle 10% of the viewport (45% to 55%)
      // This is crucial for detecting when the number is at the *center*.
      rootMargin: '-45% 0px -45% 0px', 
      threshold: 0, // We only need to detect intersection, not a threshold amount
    };

    const observer = new IntersectionObserver((entries) => {
      entries.forEach(entry => {
        const rowId = parseInt(entry.target.dataset.id);
        
        // If the row's center (number) is intersecting the middle of the screen
        if (entry.isIntersecting) {
          // Set the currently intersecting row as active/sticky
          setActiveNumberId(rowId);
        }
      });
    }, observerOptions);

    // Attach observer to each row
    rowRefs.current.forEach(ref => {
      if (ref) observer.observe(ref);
    });

    // Cleanup
    return () => {
      rowRefs.current.forEach(ref => {
        if (ref) observer.unobserve(ref);
      });
    };
  }, []);

  return (
    <SamContainer>
      {content.map((item, index) => (
        <SamRow
          key={item.id}
          ref={el => rowRefs.current[index] = el}
          data-id={item.id}
          $bgColor={item.color} 
        >
          {/* Left Column (Sticky Number) */}
          <LeftCol 
            /* Req. 6: Z-index stacking. Row 01 (index 0) gets zIndex 6. Row 06 (index 5) gets zIndex 1. */
            /* This ensures the current sticky number is always hidden by the next row's content (Req. 12) */
            style={{ zIndex: 6 - index }} 
          >
            <NumberWrapper 
                $isSticky={activeNumberId === item.id}
            >
                {item.number}
            </NumberWrapper>
          </LeftCol>

          {/* Right Column (Image) (Req. 7) */}
          <RightCol>
            <RowImage src={item.image} alt={`Content ${item.number}`} />
          </RightCol>
        </SamRow>
      ))}
    </SamContainer>
  );
};

export default Sam;
