import React, { useRef, useState, useEffect } from "react";
import illu01 from "./Frame 109.png";
import illu02 from "./Frame 109.png";
import illu03 from "./Frame 109.png";
import illu04 from "./Frame 109.png";
import illu05 from "./Frame 109.png";
import illu06 from "./Frame 109.png";

const illustrations = [illu01, illu02, illu03, illu04, illu05, illu06];

const numbers = ["01", "02", "03", "04", "05", "06"];

export default function Sam() {
  const [numberPositions, setNumberPositions] = useState(
    numbers.map(() => ({ translateY: 50, opacity: 0 }))
  );
  const sectionRefs = useRef([]);
  const containerRef = useRef(null);

  useEffect(() => {
    const handleScroll = () => {
      if (!containerRef.current) return;

      const viewportHeight = window.innerHeight;
      const viewportCenter = viewportHeight / 2;
      const scrollY = window.scrollY;
      const containerTop = containerRef.current.offsetTop;

      const newPositions = sectionRefs.current.map((ref, index) => {
        if (!ref) return { translateY: 50, opacity: 0 };

        const rect = ref.getBoundingClientRect();
        const sectionTop = rect.top;
        const sectionBottom = rect.bottom;
        const sectionCenter = sectionTop + (sectionBottom - sectionTop) / 2;

        let translateY = 50; // Start from bottom (50vh = half viewport height down from center)
        let opacity = 0;

        // Calculate distance from section center to viewport center
        const distanceFromCenter = sectionCenter - (scrollY + viewportCenter);

        // When section center is below viewport center, number moves up from bottom
        if (distanceFromCenter > 0 && distanceFromCenter < viewportHeight) {
          // Progress: 0 when section center is at viewport bottom, 1 when at viewport center
          const progress = Math.max(0, Math.min(1, 1 - distanceFromCenter / viewportHeight));
          translateY = 50 - progress * 50; // 50vh (bottom) to 0vh (center)
          opacity = progress;
        }
        // When section center reaches or passes viewport center, number sticks at center
        else if (distanceFromCenter <= 0) {
          translateY = 0; // Sticky at center
          opacity = 1;

          // Check if next row's top border is covering this number
          if (index < sectionRefs.current.length - 1) {
            const nextRef = sectionRefs.current[index + 1];
            if (nextRef) {
              const nextRect = nextRef.getBoundingClientRect();
              const nextTop = nextRect.top;

              // If next section's top has reached or passed viewport center, hide this number
              if (nextTop <= viewportCenter) {
                opacity = 0;
              }
            }
          }
        }

        return { translateY, opacity };
      });

      setNumberPositions(newPositions);
    };

    window.addEventListener("scroll", handleScroll, { passive: true });
    handleScroll();

    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <>
      <div className="sam-container" ref={containerRef}>
        {/* 6 Rows */}
        {numbers.map((number, index) => (
          <div key={index} className="sam-row">
            {/* LEFT COLUMN - 40% */}
            <div className="sam-left">
              <div className="sam-left-inner">
                {/* Number with sticky behavior */}
                <div
                  className="sam-number"
                  style={{
                    transform: `translateY(calc(-50% + ${numberPositions[index]?.translateY || 50}vh))`,
                    opacity: numberPositions[index]?.opacity || 0,
                    zIndex: index + 1, // Z-index 1 to 6
                  }}
                >
                  {number}
                </div>
                {/* Top border mask to hide previous number */}
                {index > 0 && (
                  <div className="sam-top-border-mask" style={{ zIndex: index + 2 }} />
                )}
              </div>
            </div>

            {/* RIGHT COLUMN - 60% */}
            <div className="sam-right">
              <div
                className="sam-right-section"
                ref={(el) => (sectionRefs.current[index] = el)}
              >
                <img src={illustrations[index]} className="sam-image" alt={`Illustration ${index + 1}`} />
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* CSS */}
      <style>{`
        .sam-container {
          width: 100%;
          min-height: 600vh;
          background: #000;
        }

        .sam-row {
          display: flex;
          height: 100vh;
          width: 100%;
        }

        /* LEFT COLUMN - 40% */
        .sam-left {
          width: 40%;
          position: relative;
          background: #000;
          border-top: 1px solid #fff;
          border-bottom: 1px solid #fff;
          overflow: hidden;
        }

        .sam-left-inner {
          position: relative;
          width: 100%;
          height: 100%;
        }

        .sam-number {
          position: absolute;
          top: 50%;
          right: 10%;
          font-size: 120px;
          font-weight: 300;
          color: #fff;
          white-space: nowrap;
          transition: transform 0.6s cubic-bezier(0.4, 0, 0.2, 1), opacity 0.6s cubic-bezier(0.4, 0, 0.2, 1);
          pointer-events: none;
        }

        /* Top border mask to hide previous number */
        .sam-top-border-mask {
          position: absolute;
          top: 0;
          left: 0;
          width: 100%;
          height: 50vh;
          background: #000;
          border-bottom: 1px solid #fff;
          pointer-events: none;
        }

        /* RIGHT COLUMN - 60% */
        .sam-right {
          width: 60%;
          background: #000;
          border-left: 1px solid #fff;
        }

        .sam-right-section {
          width: 100%;
          height: 100vh;
          display: flex;
          justify-content: center;
          align-items: center;
        }

        .sam-image {
          max-width: 75%;
          max-height: 75%;
          object-fit: contain;
        }
      `}</style>
    </>
  );
}
