// Sam.js
import React, { useRef, useEffect, useState } from 'react';
import styled, { css } from 'styled-components';

// --- 1. CONFIGURATION AND DATA (Same as previous) ---
const ROW_COLORS = [
    '#0e0d0d', '#250c0c', '#0c251e', 
    '#25240c', '#0c1525', '#250c25',
];
const dummyImage = "https://via.placeholder.com/600x800.png?text=Image";
const content = [
  { id: 1, number: '01', image: `${dummyImage}+01`, color: ROW_COLORS[0] },
  { id: 2, number: '02', image: `${dummyImage}+02`, color: ROW_COLORS[1] },
  { id: 3, number: '03', image: `${dummyImage}+03`, color: ROW_COLORS[2] },
  { id: 4, number: '04', image: `${dummyImage}+04`, color: ROW_COLORS[3] },
  { id: 5, number: '05', image: `${dummyImage}+05`, color: ROW_COLORS[4] },
  { id: 6, number: '06', image: `${dummyImage}+06`, color: ROW_COLORS[5] },
];

// --- 2. STYLED COMPONENTS (CSS-in-JS) ---
// (GlobalStyle, SamContainer, SamRow, SamCol, RightCol, RowImage remain the same)

const GlobalStyle = css` /* ... CSS content ... */ `;
const SamContainer = styled.div` /* ... CSS content ... */ `;
const SamRow = styled.div` /* ... CSS content ... */ `;
const SamCol = styled.div` /* ... CSS content ... */ `;
const RightCol = styled(SamCol)` /* ... CSS content ... */ `;
const RowImage = styled.img` /* ... CSS content ... */ `;


const LeftCol = styled(SamCol)`
  flex: 0 0 40%; 
  position: relative; 
  /* CRUCIAL: Clips anything that goes outside the 100vh row height, 
     but does NOT solve the 'hiding from the bottom' view issue. */
  overflow: hidden; 
  border-top: 1px solid white;
  border-bottom: 1px solid white; 
`;

// Number Wrapper (CRITICAL CSS MODIFICATION)
const NumberWrapper = styled.div`
  /* Default position is absolute within the 100vh row */
  position: absolute; 
  top: 50%;
  right: 5%; 
  transform: translateY(-50%); /* Center vertically within the row */
  
  font-size: 5rem;
  font-weight: bold;
  color: #444; 
  transition: color 0.3s ease, opacity 0.3s ease;
  
  /* Hiding the number by default */
  opacity: 0;

  /* State when the number is active (sticky) */
  ${props => props.$isSticky && css`
    position: fixed; 
    top: 50%;
    right: 5%; 
    transform: translateY(-50%);
    
    color: white; 
    opacity: 1;
    transition: none; /* Disable transition when switching to fixed */
  `}
  
  /* State when the number has just scrolled into view and is approaching middle */
  /* This is the state where the number is visible but not yet sticky. */
  ${props => props.$isVisible && !props.$isSticky && css`
    opacity: 1;
    color: #444; 
  `}
`;


// --- 3. React Component Logic (CRITICAL JS MODIFICATION) ---

const Sam = () => {
  const [activeNumberId, setActiveNumberId] = useState(1);
  // New state to track which numbers are visible but not yet sticky
  const [visibleNumbers, setVisibleNumbers] = useState({}); 
  const rowRefs = useRef([]);

  useEffect(() => {
    // 1. Observer for STICKY activation (Center intersection)
    const stickyObserverOptions = {
      root: null, // Viewport
      rootMargin: '-45% 0px -45% 0px', // Targets the middle 10% of the viewport
      threshold: 0, 
    };

    const stickyObserver = new IntersectionObserver((entries) => {
      entries.forEach(entry => {
        const rowId = parseInt(entry.target.dataset.id);
        
        if (entry.isIntersecting) {
          setActiveNumberId(rowId);
        }
      });
    }, stickyObserverOptions);

    // 2. Observer for VISIBILITY activation (Bottom-up intersection)
    const visibilityObserverOptions = {
        root: null,
        // When the bottom of the row hits the bottom 10% of the screen
        rootMargin: '0px 0px -90% 0px', 
        threshold: 0,
    };

    const visibilityObserver = new IntersectionObserver((entries) => {
        entries.forEach(entry => {
            const rowId = parseInt(entry.target.dataset.id);
            
            setVisibleNumbers(prev => ({
                ...prev,
                // Set true if the row is visible in the bottom 10% of the screen
                [rowId]: entry.isIntersecting, 
            }));
        });
    }, visibilityObserverOptions);

    // Attach both observers
    rowRefs.current.forEach(ref => {
      if (ref) {
          stickyObserver.observe(ref);
          visibilityObserver.observe(ref);
      }
    });

    // Cleanup
    return () => {
      rowRefs.current.forEach(ref => {
        if (ref) {
            stickyObserver.unobserve(ref);
            visibilityObserver.unobserve(ref);
        }
      });
    };
  }, []);

  return (
    <SamContainer>
      {content.map((item, index) => (
        <SamRow
          key={item.id}
          ref={el => rowRefs.current[index] = el}
          data-id={item.id}
          $bgColor={item.color} 
        >
          {/* Left Column (Sticky Number) */}
          <LeftCol 
            style={{ zIndex: 6 - index }} 
          >
            <NumberWrapper 
                $isSticky={activeNumberId === item.id}
                // Use the new state for visibility
                $isVisible={visibleNumbers[item.id]} 
            >
                {item.number}
            </NumberWrapper>
          </LeftCol>

          {/* Right Column (Image) */}
          <RightCol>
            <RowImage src={item.image} alt={`Content ${item.number}`} />
          </RightCol>
        </SamRow>
      ))}
    </SamContainer>
  );
};

export default Sam;
