// Sam.js
import React, { useRef, useEffect, useState } from 'react';
import styled, { css, keyframes } from 'styled-components';

// --- 1. Dummy Image Imports (Replace with actual paths) ---
// In a real project, replace these with your actual image paths, 
// e.g., import img01 from './assets/image01.jpg';
const dummyImage = "https://via.placeholder.com/600x800.png?text=Image";
const images = [
  `${dummyImage}+01`,
  `${dummyImage}+02`,
  `${dummyImage}+03`,
  `${dummyImage}+04`,
  `${dummyImage}+05`,
  `${dummyImage}+06`,
];

const content = [
  { id: 1, number: '01', image: images[0] },
  { id: 2, number: '02', image: images[1] },
  { id: 3, number: '03', image: images[2] },
  { id: 4, number: '04', image: images[3] },
  { id: 5, number: '05', image: images[4] },
  { id: 6, number: '06', image: images[5] },
];

// --- 2. Styled Components Definitions (CSS-in-JS) ---

// Global body styles (Req. 9)
const GlobalStyle = css`
  body {
    background-color: black;
    color: white;
    margin: 0;
    padding: 0;
  }
`;

// Main Container
const SamContainer = styled.div`
  ${GlobalStyle}
  display: block;
  width: 100%;
`;

// Row Styling (Req. 2)
const SamRow = styled.div`
  display: flex;
  height: 100vh; 
  width: 100%;
`;

// Column Base Styling
const SamCol = styled.div`
  display: flex; 
  align-items: center;
  justify-content: center;
`;

// Left Column (Req. 1, 8, 10, 11)
const LeftCol = styled(SamCol)`
  flex: 0 0 40%; /* 40% width */
  position: relative; 
  overflow: hidden; /* **CRUCIAL**: Hides the number that goes above the top border (Req. 10 & 11) */
  /* Req. 8 & 9 */
  border-top: 1px solid white;
  border-bottom: 1px solid white; 
  
  /* Setting z-index for overlapping effect (Req. 6) */
  /* This is applied dynamically in the component using a style prop */
`;

// Right Column (Req. 1, 4, 7)
const RightCol = styled(SamCol)`
  flex: 0 0 60%; /* 60% width */
`;

// Image Styling (Req. 4)
const RowImage = styled.img`
  max-width: 80%;
  max-height: 80%;
  object-fit: contain;
`;

// Number Wrapper (Req. 3, 5, 11)
const NumberWrapper = styled.div`
  /* Default state: positioned for the scroll effect */
  position: absolute; 
  top: 50%;
  right: 5%; /* Req. 5: At center right */
  
  font-size: 5rem;
  font-weight: bold;
  color: #444; /* Dim number when not active */
  transition: color 0.3s ease;
  
  /* Initial state: Hidden below the screen center (Req. 11) */
  opacity: 0;
  transform: translate(0, 100vh); 

  /* Active/Sticky State (Req. 5) */
  ${props => props.$isSticky && css`
    /* Override initial transform to make it 'sticky' in the middle */
    position: fixed; 
    top: 50%;
    right: 5%; 
    transform: translateY(-50%);
    
    color: white; /* Highlight active number */
    opacity: 1;
    
    /* Disabling transform transition to make it immediately 'fixed' */
    transition: opacity 0.3s, color 0.3s;
  `}
`;

// --- 3. React Component Logic ---

const Sam = () => {
  const [activeNumberId, setActiveNumberId] = useState(1);
  const rowRefs = useRef([]);

  useEffect(() => {
    // Intersection Observer Configuration
    const observerOptions = {
      root: null, // Viewport
      // Target an area covering the middle 10% of the viewport (45% to 55%)
      // This is crucial for detecting when the number is at the *center*.
      rootMargin: '-45% 0px -45% 0px', 
      threshold: 0, 
    };

    const observer = new IntersectionObserver((entries) => {
      entries.forEach(entry => {
        const rowId = parseInt(entry.target.dataset.id);
        
        // If the row's number area is intersecting the middle of the screen
        if (entry.isIntersecting) {
          setActiveNumberId(rowId);
        }
      });
    }, observerOptions);

    // Attach observer to each row
    rowRefs.current.forEach(ref => {
      if (ref) observer.observe(ref);
    });

    // Cleanup
    return () => {
      rowRefs.current.forEach(ref => {
        if (ref) observer.unobserve(ref);
      });
    };
  }, []);

  return (
    <SamContainer>
      {content.map((item, index) => (
        <SamRow
          key={item.id}
          ref={el => rowRefs.current[index] = el}
          data-id={item.id}
        >
          {/* Left Column (Sticky Number) */}
          <LeftCol 
            // Setting z-index dynamically (Req. 6)
            style={{ zIndex: 6 - index }} 
          >
            <NumberWrapper 
                // Passed as transient prop $isSticky to avoid being passed to the DOM
                $isSticky={activeNumberId === item.id}
            >
                {item.number}
            </NumberWrapper>
          </LeftCol>

          {/* Right Column (Image) (Req. 7) */}
          <RightCol>
            <RowImage src={item.image} alt={`Content ${item.number}`} />
          </RightCol>
        </SamRow>
      ))}
    </SamContainer>
  );
};

export default Sam;
