import { useEffect, useState, useRef } from "react";
import "./hiw.css";

function Hiw() {
  const [activeIndex, setActiveIndex] = useState(0);
  const wrapperRef = useRef(null);

  useEffect(() => {
    const handleScroll = () => {
      if (!wrapperRef.current) return;

      const rect = wrapperRef.current.getBoundingClientRect();
      const viewHeight = window.innerHeight;
      
      // Calculate how far we have scrolled through the section
      // 0 = start of section, 1 = end of section
      const progress = Math.abs(rect.top) / (rect.height - viewHeight);
      
      // Determine index (0, 1, or 2)
      let index = Math.floor(progress * 3);
      
      // Clamp values
      if (index < 0) index = 0;
      if (index > 2) index = 2;

      setActiveIndex(index);
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <div className="hiw-wrapper" ref={wrapperRef}>
      <div className="hiw-container">
        
        <div className="lft">
          <p className={activeIndex === 0 ? "active" : ""}>01 Step One Description</p>
          <p className={activeIndex === 1 ? "active" : ""}>02 Step Two Description</p>
          <p className={activeIndex === 2 ? "active" : ""}>03 Step Three Description</p>
        </div>

        <div className="rgt">
          <div className={`hiwimage ${activeIndex === 0 ? "show" : ""}`}>
            {/* Replace with your <img> tag */}
            <div style={{width: '300px', height: '300px', background: 'red'}}>IMAGE 1</div>
          </div>
          <div className={`hiwimage ${activeIndex === 1 ? "show" : ""}`}>
            <div style={{width: '300px', height: '300px', background: 'blue'}}>IMAGE 2</div>
          </div>
          <div className={`hiwimage ${activeIndex === 2 ? "show" : ""}`}>
            <div style={{width: '300px', height: '300px', background: 'green'}}>IMAGE 3</div>
          </div>
        </div>

      </div>
    </div>
  );
}

export default Hiw;
