// Sam.js (FINAL UPDATED FIXED VERSION)
import React, { useRef, useEffect, useState } from 'react';
import styled, { css } from 'styled-components';

// -------------------------------------
// 1. CONFIGURATION
// -------------------------------------

const ROW_COLORS = [
    '#0e0d0d',
    '#250c0c',
    '#0c251e',
    '#25240c',
    '#0c1525',
    '#250c25',
];

const dummyImage = "https://via.placeholder.com/600x800.png?text=Image";
const images = [
  `${dummyImage}+01`,
  `${dummyImage}+02`,
  `${dummyImage}+03`,
  `${dummyImage}+04`,
  `${dummyImage}+05`,
  `${dummyImage}+06`,
];

const content = [
  { id: 1, number: '01', image: images[0], color: ROW_COLORS[0] },
  { id: 2, number: '02', image: images[1], color: ROW_COLORS[1] },
  { id: 3, number: '03', image: images[2], color: ROW_COLORS[2] },
  { id: 4, number: '04', image: images[3], color: ROW_COLORS[3] },
  { id: 5, number: '05', image: images[4], color: ROW_COLORS[4] },
  { id: 6, number: '06', image: images[5], color: ROW_COLORS[5] },
];

// -------------------------------------
// 2. STYLED COMPONENTS
// -------------------------------------

const SamContainer = styled.div`
  width: 100%;
  background: black;
  color: white;
`;

const SamRow = styled.div`
  display: flex;
  height: 100vh;
  width: 100%;
  background-color: ${(props) => props.$bgColor};
`;

const SamCol = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
`;

const LeftCol = styled(SamCol)`
  flex: 0 0 40%;
  position: relative;
  overflow: hidden;
  border-top: 1px solid white;
  border-bottom: 1px solid white;
`;

const RightCol = styled(SamCol)`
  flex: 0 0 60%;
  border-top: 1px solid white;
  border-bottom: 1px solid white;
`;

const RowImage = styled.img`
  width: 70%;
  height: auto;
  object-fit: contain;
`;

const NumberWrapper = styled.div`
  position: absolute;
  top: 50%;
  right: 10%;
  font-size: 6rem;
  font-weight: 700;

  /* Start INSIDE the component (slightly lowered) */
  opacity: 0;
  transform: translateY(40px);
  transition: opacity 0.35s ease-out, transform 0.35s ease-out, color 0.35s ease-out;

  ${(props) =>
    props.$isSticky &&
    css`
      position: fixed;
      top: 50%;
      right: 10%;
      transform: translateY(-50%);
      opacity: 1;
      color: white;
  `}
`;

// -------------------------------------
// 3. COMPONENT LOGIC
// -------------------------------------

const Sam = () => {
  const rowRefs = useRef([]);
  const [activeNumberId, setActiveNumberId] = useState(1);

  useEffect(() => {
    const observerOptions = {
      root: null,
      // Activates earlier so 01 appears immediately
      rootMargin: "-30% 0px -30% 0px",
      threshold: 0,
    };

    const observer = new IntersectionObserver((entries) => {
      entries.forEach((entry) => {
        const id = Number(entry.target.dataset.id);
        if (entry.isIntersecting) {
          setActiveNumberId(id);
        }
      });
    }, observerOptions);

    rowRefs.current.forEach((ref) => {
      if (ref) observer.observe(ref);
    });

    return () => observer.disconnect();
  }, []);

  return (
    <SamContainer>
      {content.map((item, index) => (
        <SamRow
          key={item.id}
          data-id={item.id}
          $bgColor={item.color}
          ref={(el) => (rowRefs.current[index] = el)}
          style={{ zIndex: 6 - index }}
        >
          {/* LEFT COLUMN NUMBER */}
          <LeftCol>
            <NumberWrapper $isSticky={activeNumberId === item.id}>
              {item.number}
            </NumberWrapper>
          </LeftCol>

          {/* RIGHT COLUMN IMAGE */}
          <RightCol>
            <RowImage src={item.image} alt={`Row ${item.number}`} />
          </RightCol>
        </SamRow>
      ))}
    </SamContainer>
  );
};

export default Sam;
