import React, { useEffect, useRef, useState } from "react";

export default function Sam() {
  const rightRefs = useRef([]);
  const [activeIndex, setActiveIndex] = useState(0);

  const content = [
    { title: "Creative Strategy", body: "Brand direction, storytelling, and deep discovery insights." },
    { title: "Design Execution", body: "Stunning visuals crafted for modern digital experiences." },
    { title: "Motion & Interaction", body: "Smooth transitions and immersive micro-interactions." },
    { title: "Development", body: "High-performance websites with scalable architecture." },
    { title: "Launch & Support", body: "Optimization, testing, deployment and ongoing support." }
  ];

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            const index = Number(entry.target.dataset.index);
            setActiveIndex(index);
          }
        });
      },
      {
        threshold: 0.5,
      }
    );

    rightRefs.current.forEach((el) => observer.observe(el));

    return () => observer.disconnect();
  }, []);

  return (
    <>
      <div className="container">

        {/* LEFT STICKY PANEL */}
        <div className="left-panel">
          <div className="left-content">
            <h1 className="left-number">{String(activeIndex + 1).padStart(2, "0")}</h1>
            <h2 className="left-title">{content[activeIndex].title}</h2>
            <p className="left-desc">{content[activeIndex].body}</p>
          </div>
        </div>

        {/* RIGHT SIDE SCROLLING SECTIONS */}
        <div className="right-panel">
          {content.map((item, i) => (
            <div
              key={i}
              data-index={i}
              className={`section ${activeIndex === i ? "active" : ""}`}
              ref={(el) => (rightRefs.current[i] = el)}
            >
              <div className="box">
                <h1>{item.title}</h1>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* INLINE CSS */}
      <style>{`
        body {
          margin: 0;
          padding: 0;
          background: #000;
          color: white;
          font-family: Arial, sans-serif;
        }

        .container {
          display: flex;
          width: 100%;
          min-height: 600vh;
        }

        /* LEFT PANEL (STICKY) */
        .left-panel {
          width: 40%;
          height: 100vh;
          position: sticky;
          top: 0;
          display: flex;
          align-items: center;
          padding-left: 50px;
          background: #000;
          border-right: 1px solid #333;
        }

        .left-number {
          font-size: 100px;
          opacity: 0.3;
          margin: 0;
        }

        .left-title {
          font-size: 40px;
          margin: 10px 0;
          text-transform: uppercase;
        }

        .left-desc {
          width: 80%;
          font-size: 20px;
          opacity: 0.8;
          line-height: 1.6;
        }

        /* RIGHT PANEL */
        .right-panel {
          width: 60%;
        }

        .section {
          height: 100vh;
          display: flex;
          justify-content: center;
          align-items: center;
          transition: transform 0.6s ease, opacity 0.6s ease;
          opacity: 0.35;
          transform: translateY(120px);
        }

        .section.active {
          opacity: 1;
          transform: translateY(0px);
        }

        .box {
          width: 70%;
          height: 60%;
          border: 2px solid #fff;
          display: flex;
          justify-content: center;
          align-items: center;
          font-size: 40px;
          text-transform: uppercase;
        }
      `}</style>
    </>
  );
}
