// Sam.js
import React, { useRef, useState, useEffect } from "react";
import illu01 from "./Frame 109.png";
import illu02 from "./Frame 109.png";
import illu03 from "./Frame 109.png";
import illu04 from "./Frame 109.png";
import illu05 from "./Frame 109.png";
import illu06 from "./Frame 109.png";
import signup from "./Frame.png";

const illustrations = [illu01, illu02, illu03, illu04, illu05, illu06];

// Colors (easy to change)
const bgColors = ["black","black","black","black","black","black"];
const borderColor = "white";

const content = [
  { number: "01", heading: "Your data stays yours", body: "Your health data is encrypted, never sold, and never shared without your permission." },
  { number: "02", heading: "Built for safety first", body: "AI Doctor avoids harmful or risky recommendations and escalates to clinicians." },
  { number: "03", heading: "Transparent and in your control", body: "You can see why the AI makes decisions. No black boxes." },
  { number: "04", heading: "Medically validated", body: "Every AI Doctor is reviewed by licensed physicians." },
  { number: "05", heading: "Regulation compliant", body: "Complies with HIPAA and medical AI safeguards." },
  { number: "06", heading: "Human doctors oversee everything", body: "Real doctors monitor all cases alongside the AI." }
];

export default function Sam() {
  const sectionRefs = useRef([]);
  const [sections, setSections] = useState([]);

  // Update active map on scroll
  useEffect(() => {
    const handleScroll = () => {
      const vh = window.innerHeight;
      const updated = sectionRefs.current.map((ref) => {
        if (!ref) return { isActive: false };
        const rect = ref.getBoundingClientRect();
        const isActive = rect.top <= vh / 2 && rect.bottom >= vh / 2;
        return { isActive };
      });
      setSections(updated);
    };

    window.addEventListener("scroll", handleScroll, { passive: true });
    handleScroll();
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  // find which index is active; fallback to 0
  const activeIndex = sections.findIndex(s => s.isActive);
  const fallbackIndex = activeIndex === -1 ? 0 : activeIndex;

  return (
    <>
      <div className="scroll-carousel">
        {/* LEFT: stacked cards (sticky area) */}
        <div className="carousel-left">
          {content.map((item, index) => {
            const isActive = index === fallbackIndex;
            const isPast = index < fallbackIndex;
            const isFuture = index > fallbackIndex;

            // Off-screen positions for before/after
            let transformVal = "translateY(0)"; // active
            if (isFuture) transformVal = "translateY(100vh)";   // below screen
            if (isPast)   transformVal = "translateY(-100vh)";  // above screen

            // zIndex: later cards should overlay earlier ones
            const z = index + 1; // 1..6

            return (
              <div
                key={index}
                className="content-card"
                style={{
                  background: bgColors[index],
                  zIndex: z,
                  borderTop: `2px solid ${borderColor}`,
                  borderBottom: `2px solid ${borderColor}`,
                  transform: transformVal,
                  transition: "transform 900ms ease-in-out, opacity 150ms linear",
                  opacity: isActive ? 1 : 0
                }}
              >
                <div className="left-inner">
                  {/* Number (center-right inside the left column) */}
                  <div className="number-wrap">
                    <span className="big-number">{item.number}</span>
                  </div>

                  {/* content (top-aligned so border touches content) */}
                  <div className="text-block">
                    <img src={signup} alt="" className="signup-ico" />
                    <div className="gradient-border" />
                    <h3 className="lef-title">{item.heading}</h3>
                    <p className="lef-desc">{item.body}</p>
                  </div>
                </div>
              </div>
            );
          })}
        </div>

        {/* RIGHT: static column with image sections */}
        <div className="carousel-right">
          {illustrations.map((img, i) => (
            <section
              className="content-section"
              key={i}
              ref={(el) => (sectionRefs.current[i] = el)}
            >
              <img src={img} alt={`illu-${i}`} className="illu" />
            </section>
          ))}
        </div>
      </div>

      {/* Styles */}
      <style>{`
        :root {
          --left-w: 40vw;
          --right-w: 60vw;
        }

        * { box-sizing: border-box; }

        .scroll-carousel {
          display: flex;
          width: 100%;
          min-height: 600vh;
          background: #000;
          color: white;
        }

        /* LEFT column: sticky area */
        .carousel-left {
          width: var(--left-w);
          height: 100vh;
          position: sticky;
          top: 0;
          overflow: hidden;
          /* keep stacking context */
          display: block;
        }

        /* Each card occupies full sticky area and is absolutely stacked */
        .content-card {
          position: absolute;
          left: 0;
          top: 0;
          width: var(--left-w);
          height: 100vh;
          padding: 0;
          margin: 0;
          box-sizing: border-box;
          color: white;
          pointer-events: none; /* optional */
        }

        /* inner layout: number + text */
        .left-inner {
          position: relative;
          height: 100%;
          width: 100%;
        }

        /* Number wrapper: center vertically (middle of viewport), placed right inside left column */
        .number-wrap {
          position: absolute;
          right: 10%;               /* adjust to place number near center-right of left column */
          top: 50%;
          transform: translateY(-50%);
          pointer-events: none;
          z-index: 5;
        }

        .big-number {
          font-size: 90px;
          font-weight: 300;
          line-height: 1;
          color: white;
          display: block;
          white-space: nowrap;
        }

        /* Text block starts from top so border touches the content */
        .text-block {
          position: absolute;
          top: 30px; /* small gap from top border */
          left: 4%;
          right: 12%;
        }

        .signup-ico {
          width: 48px;
          display: block;
          margin-bottom: 12px;
        }

        .gradient-border {
          width: 100%;
          height: 2px;
          background: linear-gradient(to right, #777, #000);
          margin: 14px 0 18px;
        }

        .lef-title {
          margin: 0 0 8px;
          font-size: 28px;
          letter-spacing: 0.6px;
          text-transform: uppercase;
        }

        .lef-desc {
          margin: 0;
          font-size: 16px;
          color: #cfcfcf;
          line-height: 1.45;
        }

        /* RIGHT column: non-sticky image sections */
        .carousel-right {
          width: var(--right-w);
          border-left: 1px solid ${borderColor};
        }

        .content-section {
          height: 100vh;
          display: flex;
          align-items: center;
          justify-content: center;
          background: black;
        }

        .illu {
          width: 70%;
          max-width: 900px;
        }

        /* small responsive tweak */
        @media (max-width: 900px) {
          :root { --left-w: 45vw; --right-w: 55vw; }
          .big-number { font-size: 64px; }
        }
      `}</style>
    </>
  );
}
