import React from 'react';
import styled, { css, createGlobalStyle } from 'styled-components';

// --- 1. CONFIGURATION AND DATA ---

// Define colors for each row 
const ROW_COLORS = [
    '#0e0d0d', 
    '#250c0c', 
    '#0c251e', 
    '#25240c', 
    '#0c1525', 
    '#250c25',
];

// Dummy Image URLs (for demonstration)
const dummyImage = "https://via.placeholder.com/600x800.png?text=Image";
const content = [
    { id: 1, number: '01', image: `${dummyImage}+01`, color: ROW_COLORS[0] },
    { id: 2, number: '02', image: `${dummyImage}+02`, color: ROW_COLORS[1] },
    { id: 3, number: '03', image: `${dummyImage}+03`, color: ROW_COLORS[2] },
    { id: 4, number: '04', image: `${dummyImage}+04`, color: ROW_COLORS[3] },
    { id: 5, number: '05', image: `${dummyImage}+05`, color: ROW_COLORS[4] },
    { id: 6, number: '06', image: `${dummyImage}+06`, color: ROW_COLORS[5] },
];

// --- 2. STYLED COMPONENTS (CSS-in-JS) ---

const GlobalStyle = createGlobalStyle`
    body {
        background-color: black; 
        color: white;
        margin: 0;
        padding: 0;
        /* Ensure smooth scrolling for the visual effect */
        scroll-behavior: smooth;
    }
`;

const SamContainer = styled.div`
    display: block;
    width: 100%;
`;

const SamRow = styled.div`
    display: flex;
    height: 100vh; 
    width: 100%;
    background-color: ${props => props.$bgColor || 'black'}; 
`;

const SamCol = styled.div`
    display: flex; 
    align-items: center;
    justify-content: center;
`;

const RightCol = styled(SamCol)`
    flex: 0 0 60%; 
`;

const RowImage = styled.img`
    max-width: 80%;
    max-height: 80%;
    object-fit: contain;
`;

/* Left Column (40%) - The container for the sticky effect
  - Uses relative position to establish a stacking context for z-index.
  - overflow: hidden to ensure the number disappears as the row boundary passes.
*/
const LeftCol = styled(SamCol)`
    flex: 0 0 40%; 
    position: relative; 
    overflow: hidden; 
    border-top: 1px solid white;
    border-bottom: 1px solid white; 
`;

/* Sticker Container (200vh tall)
  - Provides the travel distance (from bottom to middle, then out top) for the number.
*/
const StickerContainer = styled.div`
    height: 200vh;
    width: 100%;
`;

/* Number Wrapper (Sticky element)
  - position: sticky, top: 50%, transform: translateY(-50%) centers it while sticky.
*/
const NumberWrapper = styled.div`
    position: sticky; 
    top: 50%; 
    transform: translateY(-50%); 
    
    width: 100%;

    display: flex;
    justify-content: flex-end; 
    padding-right: 10%;
`;

const SamNumber = styled.div`
    font-size: 5rem;
    font-weight: bold;
    line-height: 1; 
`;


// --- 3. React Component Logic ---

const Sam = () => {
    return (
        <>
            <GlobalStyle />
            <SamContainer>
                {content.map((item, index) => (
                    <SamRow
                        key={item.id}
                        $bgColor={item.color} 
                    >
                        {/* 40% Left Column */}
                        <LeftCol 
                            // ***********************************************
                            // CRITICAL FIX: Z-Index for Overlap (Req. 6, 12)
                            // We need the next row (index + 1) to have a HIGHER z-index.
                            style={{ zIndex: index + 1 }} 
                            // ***********************************************
                        >
                            {/* Sticker Container (200vh tall) */}
                            <StickerContainer>
                                <NumberWrapper>
                                    <SamNumber>
                                        {item.number}
                                    </SamNumber>
                                </NumberWrapper>
                            </StickerContainer>
                        </LeftCol>

                        {/* 60% Right Column */}
                        <RightCol>
                            <RowImage src={item.image} alt={`Content ${item.number}`} />
                        </RightCol>
                    </SamRow>
                ))}
            </SamContainer>
        </>
    );
};

export default Sam;
