import React, { useRef, useState, useEffect } from "react";
import illu01 from "./Frame 109.png";
import illu02 from "./Frame 109.png";
import illu03 from "./Frame 109.png";
import illu04 from "./Frame 109.png";
import illu05 from "./Frame 109.png";
import illu06 from "./Frame 109.png";
import signup from "./Frame.png";

const illustrations = [illu01, illu02, illu03, illu04, illu05, illu06];

// Background colors
const bgColors = ["black", "black", "black", "black", "black", "black"];

// Border colors
const borderColors = ["white", "white", "white", "white", "white", "white"];

const content = [
  { number: "01", heading: "Your data stays yours", body: "Your health data is encrypted, never sold, and never shared without your permission." },
  { number: "02", heading: "Built for safety first", body: "AI Doctor avoids harmful or risky recommendations and escalates to clinicians." },
  { number: "03", heading: "Transparent and in your control", body: "You can see why the AI makes decisions. No black boxes." },
  { number: "04", heading: "Medically validated", body: "Every AI Doctor is reviewed by licensed physicians." },
  { number: "05", heading: "Regulation compliant", body: "Complies with HIPAA and medical AI safeguards." },
  { number: "06", heading: "Human doctors oversee everything", body: "Real doctors monitor all cases alongside the AI." }
];

export default function Sam() {
  const [sections, setSections] = useState([]);
  const sectionRefs = useRef([]);

  useEffect(() => {
    const handleScroll = () => {
      const viewportHeight = window.innerHeight;

      const updated = sectionRefs.current.map((ref) => {
        if (!ref) return { isActive: false };

        const rect = ref.getBoundingClientRect();
        const isActive =
          rect.top <= viewportHeight / 2 &&
          rect.bottom >= viewportHeight / 2;

        return { isActive };
      });

      setSections(updated);
    };

    window.addEventListener("scroll", handleScroll);
    handleScroll();

    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <>
      <div className="scroll-carousel">

        {/* LEFT FIXED PANEL */}
        <div className="carousel-left">

          {/* STICKY CENTER NUMBER */}
          <div className="sticky-number">
            {content.map((item, index) => {
              const sec = sections[index] || { isActive: index === 0 };
              return (
                <h1
                  key={index}
                  style={{
                    opacity: sec.isActive ? 1 : 0,
                    position: "absolute",
                    transition: "opacity 0.5s ease",
                  }}
                >
                  {item.number}
                </h1>
              );
            })}
          </div>

          {/* SLIDING CARDS */}
          {content.map((item, index) => {
            const sec = sections[index] || { isActive: index === 0 };

            return (
              <div
                key={index}
                className="content-card"
                style={{
                  opacity: sec.isActive ? 1 : 0,
                  zIndex: 6 - index,
                  backgroundColor: bgColors[index],
                  border: `3px solid ${borderColors[index]}`,

                  // ⭐ ALWAYS SLIDE FROM BOTTOM OF SCREEN
                  transform: sec.isActive ? "translateY(0)" : "translateY(100vh)",
                  transition: "transform 1s ease-in-out, opacity 0.5s ease"
                }}
              >
                <div className="left-wrap">
                  <img src={signup} className="signup-ico" alt="" />

                  {/* Title & Description */}
                  <div className="gradient-border" />
                  <h3 className="lef-title">{item.heading}</h3>
                  <p className="lef-desc">{item.body}</p>
                </div>
              </div>
            );
          })}

        </div>

        {/* RIGHT IMAGES */}
        <div className="carousel-right">
          {content.map((_, i) => (
            <div
              className="content-section"
              key={i}
              ref={(el) => (sectionRefs.current[i] = el)}
            >
              <img src={illustrations[i]} className="illu" alt="" />
            </div>
          ))}
        </div>

      </div>

      {/* CSS */}
      <style>{`
        .scroll-carousel {
          display: flex;
          min-height: 600vh;
          background: #000;
          width: 98%;
          border: 1px solid white;
          margin: 20px auto;
        }

        .carousel-left {
          width: 40%;
          height: 100vh;
          position: sticky;
          top: 0;
          display: flex;
          align-items: center;
          justify-content: center;
          overflow: hidden;
          position: relative;
        }

        /* STICKY NUMBER AT CENTER */
        .sticky-number {
          position: absolute;
          top: 50%;
          left: 70%;
          transform: translateY(-50%);
          font-size: 90px;
          font-weight: 300;
          color: white;
          z-index: 50;
        }

        .content-card {
          position: absolute;
          width: 100%;
          height: 100%;
          padding: 25px;
          box-sizing: border-box;
        }

        .left-wrap {
          position: absolute;
          width: 90%;
          bottom: 10%;
          left: 5%;
        }

        .signup-ico {
          width: 50px;
          margin-bottom: 20px;
        }

        .gradient-border {
          width: 100%;
          height: 2px;
          background: linear-gradient(to right, #777, #000);
          margin: 20px 0;
        }

        .lef-title {
          font-size: 36px;
          color: white;
          margin-bottom: 10px;
        }

        .lef-desc {
          width: 90%;
          font-size: 18px;
          opacity: 0.75;
          color: #ccc;
        }

        .carousel-right {
          width: 60%;
          border-left: 1px solid white;
        }

        .content-section {
          height: 100vh;
          display: flex;
          justify-content: center;
          align-items: center;
        }

        .illu {
          width: 75%;
        }
      `}</style>
    </>
  );
}
