// Sam.js
import React, { useRef, useEffect, useState } from "react";
import styled, { css } from "styled-components";

// IMPORT IMAGES
import img1 from "./img1.png";
import img2 from "./img2.png";
import img3 from "./img3.png";
import img4 from "./img4.png";
import img5 from "./img5.png";
import img6 from "./img6.png";

const images = [img1, img2, img3, img4, img5, img6];

/* --------------------------- STYLED COMPONENTS --------------------------- */

const Container = styled.div`
  width: 100%;
  background: black;
  overflow-x: hidden;
`;

const Row = styled.div`
  display: flex;
  height: 100vh;
  width: 100%;
  position: relative;
  background: black;
  overflow: hidden; /* Required for hiding overlapping number */
`;

const Left = styled.div`
  width: 40%;
  border-top: 1px solid white;
  border-bottom: 1px solid white;
  color: white;
  display: flex;
  justify-content: flex-end;
  align-items: center;
  position: relative;
`;

const Right = styled.div`
  width: 60%;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const Img = styled.img`
  max-width: 80%;
  height: auto;
`;

const Number = styled.div`
  font-size: 8rem;
  font-weight: 700;
  opacity: 0.18;
  position: absolute;
  bottom: -40%; /* Start from bottom */
  right: 10%;
  transition: all 0.4s linear;

  ${(props) =>
    props.sticky &&
    css`
      position: sticky;
      top: 50%;
      transform: translateY(-50%);
      bottom: unset;
      opacity: 1;
    `}
`;

/* ------------------------------------------------------------------------- */

export default function Sam() {
  const rowsRef = useRef([]);
  const [activeIndex, setActiveIndex] = useState(0);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            const id = Number(entry.target.dataset.index);
            setActiveIndex(id);
          }
        });
      },
      { threshold: 0.5 }
    );

    rowsRef.current.forEach((row) => observer.observe(row));

    return () => observer.disconnect();
  }, []);

  return (
    <Container>
      {images.map((image, index) => (
        <Row
          key={index}
          data-index={index}
          ref={(el) => (rowsRef.current[index] = el)}
          style={{ zIndex: index + 1 }}
        >
          {/* LEFT COLUMN */}
          <Left>
            <Number sticky={activeIndex === index}>
              {String(index + 1).padStart(2, "0")}
            </Number>
          </Left>

          {/* RIGHT COLUMN */}
          <Right>
            <Img src={image} />
          </Right>
        </Row>
      ))}
    </Container>
  );
}
