import { useEffect, useState, useRef } from "react";
import "./hiw.css";
import i1 from "./i1.png";
import i2 from "./i2.png";
import i3 from "./i3.png";
import { FiArrowUpRight } from "react-icons/fi";

function Hiw() {
  const [activeIndex, setActiveIndex] = useState(0);
  const wrapperRef = useRef(null);

  useEffect(() => {
    const handleScroll = () => {
      if (!wrapperRef.current) return;

      const rect = wrapperRef.current.getBoundingClientRect();
      const scrollProgress = -rect.top;
      const wrapperHeight = rect.height - window.innerHeight;

      let progress = scrollProgress / wrapperHeight;
      let index = Math.floor(progress * 3);

      if (index < 0) index = 0;
      if (index > 2) index = 2;

      setActiveIndex(index);
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const steps = [
    {
      num: "01",
      text: "Your AI Doctor studies the way your body behaves",
      icon: i1,
    },
    {
      num: "02",
      text: "Behind every insight is real clinical reasoning.",
      icon: i2,
    },
    {
      num: "03",
      text: "Clear explanations. Simple actions. No jargon.",
      icon: i3,
    },
  ];

  return (
    <div className="hiw-wrapper" ref={wrapperRef}>
      <div className="hiw-container">

        {/* LEFT */}
        <div className="lft">
          {steps.map((step, i) => (
            <div
              key={i}
              className={`lft-row ${activeIndex === i ? "active" : ""}`}
            >
              <div className="lft-num">{step.num}</div>
              <div className="lft-text">{step.text}</div>
              <div className="lft-icon">
                 <FiArrowUpRight size={28} c/>
              </div>
            </div>
          ))}
        </div>

        {/* RIGHT */}
        <div className="rgt">
          <img src={i1} className={`hiwimage ${activeIndex === 0 ? "show" : ""}`} />
          <img src={i2} className={`hiwimage ${activeIndex === 1 ? "show" : ""}`} />
          <img src={i3} className={`hiwimage ${activeIndex === 2 ? "show" : ""}`} />
        </div>

      </div>
    </div>
  );
}

export default Hiw;
