import React from 'react';

// Import your images here. Replace these with your actual image paths.
// For demonstration, I'm using placeholder names.
import image01 from './iPhone.png';
import image02 from './images/img-02.jpg';
import image03 from './images/img-03.jpg';
import image04 from './images/img-04.jpg';
import image05 from './images/img-05.jpg';
import image06 from './images/img-06.jpg';

const data = [
  { number: '01', image: image01 },
  { number: '02', image: image02 },
  { number: '03', image: image03 },
  { number: '04', image: image04 },
  { number: '05', image: image05 },
  { number: '06', image: image06 },
];

/* ⚠️ NOTE: The 'position: sticky' CSS rule cannot be applied using standard React inline styles 
  (e.g., style={{ position: 'sticky' }}), as inline styles do not support it correctly.
  The most reliable way to enforce the sticky and overlapping effect without an external CSS file 
  is to use a dedicated CSS-in-JS library like Styled Components or Emotion, or embed a <style> tag, 
  as shown below.

  This approach is often discouraged but fulfills the requirement to have the CSS within Sam.js.
*/

// --- CSS AS A STRING (Simulating the Content of Sam.css) ---
const styles = `
.sam-container {
  background-color: black;
  color: white;
}

.sam-row {
  display: flex;
  height: 100vh; /* 2) Each row is 100vh */
  border-bottom: 1px solid white; 
}
/* The top border is applied to the first element outside the loop for clean single border lines */
.sam-row:first-child {
  border-top: 1px solid white; 
}

.sam-col {
  display: flex;
  justify-content: center;
  align-items: center;
  overflow: hidden; /* Prevent sticky content from affecting row layout */
}

.sam-left {
  width: 40%; /* 1) 40% column */
  /* This border acts as the right separation and the side border for the left column */
  border-right: 1px solid white; 
}

.sam-right {
  width: 60%; /* 1) 60% column */
}

/* 4) Each right row has a single image at center */
.sam-image {
  max-width: 90%;
  max-height: 90%;
  object-fit: contain;
}

/* 5) Sticky implementation for the number:
  'position: sticky' and 'top: 0' are essential here.
*/
.sam-number-wrapper {
  height: 100vh;
  width: 100%;
  position: sticky; /* 5) The key for the sticky effect */
  top: 0;           /* 5) Sticks to the top of the viewport */
  display: flex;
  justify-content: flex-end; /* Initial position for the number wrapper */
  align-items: center;
  padding-right: 20%; /* Fine-tune position to be at 'center right' */
}

/* 3) Each Left row have Number from 01-06 at center */
.sam-number {
  font-size: 5rem;
  font-weight: bold;
}
`;
// -----------------------------------------------------------

const Sam = () => {
  return (
    <>
      {/* Injecting CSS using a <style> tag. 
        This is a workaround to apply styles that cannot be done with inline styles (like 'position: sticky').
        This should be placed in the document head, but for component encapsulation, it's here.
      */}
      <style>{styles}</style>
      
      <div className="sam-container">
        {data.map((item, index) => (
          <div key={index} className="sam-row">
            {/* The left column provides the scrollable space for the sticky number */}
            <div className="sam-col sam-left">
              {/* The zIndex is critical for the overlapping effect (6)
                It is applied here using a dynamic inline style since it changes per row.
              */}
              <div 
                className="sam-number-wrapper" 
                style={{ zIndex: data.length - index }} // Higher zIndex for lower items to overlap
              >
                <div className="sam-number">{item.number}</div>
              </div>
            </div>

            {/* The right column holds the constant image (7) */}
            <div className="sam-col sam-right">
              <img src={item.image} alt={`Content ${item.number}`} className="sam-image" />
            </div>
          </div>
        ))}
      </div>
    </>
  );
};

export default Sam;
